function checkFloatNoEmpty(field,cod){
         // El campo XXXX debe tener el formato YYYY.YY
    sizeInt=(sizeOfCTEXT-1)-numDecimales;
    var strINT = 'Y';
    var strDES = 'Y';
    for(i = 1; i < sizeInt; i++)
        strINT += 'Y';
    for(j = 1; j < numDecimales; j++)
        strDES += 'Y';
    var sep = _get("hid_SeparadorDecimales");
    if(_get(field)!=""){
      if(ValidaFloat(_get(field),sizeInt,numDecimales,entornoSeparador)!="OK"){
          cdos_mostrarAlert(GestionarMensaje(cod, strINT+sep+strDES, null, null)); 
                   _foc(field); 
                   return true;
           }
    }
}
//--------------------------------------------------------------------------------------------
function checkNumNoEmpty(field, cod){
         if(_get(field)!=""){
            if(ValidaNum(_get(field))!="OK")  {
                   cdos_mostrarAlert(GestionarMensaje(cod, null , null, null));
                   _foc(field);
                   return true;
            }
    }
}
