function checkRequiredField(field, cod){
         //alert(field + "  :  " + _get("hid_"+ field +"_req"));
         if(_get("hid_"+ field +"_req") == "REQ" && _get(field) == ''){
      cdos_mostrarAlert(GestionarMensaje(cod, null, null, null));
           //GestionarMensaje(cod, null, null, null);
           _foc(field); 
           return true;
         }
}

function checkRequired(field, cod){
         if(_get(field) == ''){
      cdos_mostrarAlert(GestionarMensaje(cod, null, null, null));
           //GestionarMensaje(cod, null, null, null);
           _foc(field); 
           return true;
         }
}
