   arr = new Array();

   arr[arr.length] = new Array('oidPais', get('frmKitProductos.hid_oidPais'));
   arr[arr.length] = new Array('oid', get('frmKitProductos.cbMarca2'));

   recargaCombo('frmKitProductos.cbMarca', 'SEGRecuperarMarcasProductoMP', 'es.indra.sicc.util.DTOOID', arr );



}
