PAGINA.JAVASCRIPT:


         var objActividadClick=null;

         //Carga el estado inicial
         function onLoadPag() {
                  try{
                           tdContenedor.onresize();
                  }catch(e){
                           ;
                  }
                  try{
                           tdContenedorDos.onresize();
                  }catch(e){
                           ;
                  }
                  configurarMenuSecundario("frmInsertarMatrizDias");
                  fMostrarMensajeError();
                  DrdEnsanchaConMargenDcho('lstFueraPeriodo',24);
                  DrdEnsanchaConMargenDcho('lstOtraCampana',24);
                  if (get('frmInsertarMatrizDias.hListaFueraPeriodoMostrar') == "SI") {
                           mostrarListaEditable();
                  } else {		
                           ocultarListaEditable();
                  }
                  eval (ON_RSZ);  
                  // deshabilitamos el manejo de requeridos por druida
                  setMV('frmInsertarMatrizDias.cbMarcas','N');
                  setMV('frmInsertarMatrizDias.cbCanales','N');
                  setMV('frmInsertarMatrizDias.cbGrupoDeZonas','N');
                  setMV('frmInsertarMatrizDias.cbGrupoDeZonasSeleccionados','N');
                  setMV('frmInsertarMatrizDias.cbActividades','N');

				  var accion = get('frmInsertarMatrizDias.accion');
				  // Cambiamos el estado del boton para cambio de modo visual
				  if ((accion == "insertaGrupo") || (accion == "insertaActividad") 
						|| (accion == "") || (accion == "eliminar")) {
					  // Si la accion fue insertaGrupo, insertaActividad, modificar eq "" o eliminar entonces deshabilito
					  deshabilitarHabilitarBoton('botonContenido','btnModoGrafico','D');
					  deshabilitarHabilitarBoton('botonContenido','btnModoNoGrafico','D');
				  } else {
					  // Si la accion fue verMatriz, crearNoGrafica, crearGrafica o buscar entonces habilito 
					  deshabilitarHabilitarBoton('botonContenido','btnModoGrafico','A');
					  deshabilitarHabilitarBoton('botonContenido','btnModoNoGrafico','A');
				  }

				  // Luego de crear una matrizDias solo dejamos habilitado el boton para cambio de modo visual (inc 10707)
				  if (get('frmInsertarMatrizDias.hMostrarCreada') == "si") {
						// dehabilitamos todos los botones salvo 
						btnProxy('1','0');
						deshabilitarHabilitarBoton('botonContenido','btnBuscar','D');
						deshabilitarHabilitarBoton('botonContenido','btnAnyadir','D');	
						deshabilitarHabilitarBoton('botonContenido','btnAnyadirActividad','D');		
						deshabilitarHabilitarBoton('botonContenido','btnModificar1','D');		
						deshabilitarHabilitarBoton('botonContenido','btnEliminar1','D');			
						deshabilitarHabilitarBoton('botonContenido','btnModificarFueraPeriodo','D');
						deshabilitarHabilitarBoton('botonContenido','btnEliminarFueraPeriodo','D');
						deshabilitarHabilitarBoton('botonContenido','btnModificarOtraCampana','D');
						deshabilitarHabilitarBoton('botonContenido','btnEliminarOtraCampana','D');
				  }
		 }


         /*
         * Inicializa la pagina
         */
         function inicializacion() {		
                  txt_to('lblValorPais', get('frmInsertarMatrizDias.hPaisDescripcion')); 
                  posicionarCombo(); 
                  focaliza('frmInsertarMatrizDias.cbMarcas','');
         }


         /*
         * Se llama a esta función javascript cuando se pulsa sobre el botón btnBuscar. 
         * Llama a LPMantieneMatrizDias con accion = "buscar".
         */
         function onClickBuscar() {
                  // validamos campos requeridos
                  if( ! sicc_validaciones_generales('grupoBuscar') ) {
                           return false;
                  }		

                  // tomamos valores seleccionados
                  obtenerMarcaCanal();

                  // si hay modificaciones por perder
                  if ( get_visibilidad('capaSeleccion') ) {
                           // alertamos sobre perdida de modificaciones
                           if ( ! GestionarMensaje(751) ) {
                                    return;
                           }
                  }

                  //ejecutamos la accion buscar
                  set('frmInsertarMatrizDias.accion', 'buscar');
                  enviaSICC('frmInsertarMatrizDias');
         }


         /*
         * Se llama a esta función javascript cuando se pulsa sobre el botón btnAnyadir. 
         * Llama a LPMantieneMatrizDias con accion = "insertaGrupo"
         */
        function onClickAnyadir() {
                  // validamos campos requeridos
                  if( ! sicc_validaciones_generales('grupoAnyadirGrupoZona') ) {
                           return false;
                  }

                  //obtenemos los oids multiselección de cbGrupoDeZonas
                  var arrOidsGruposAnyadir = get('frmInsertarMatrizDias.cbGrupoDeZonas');
                  set('frmInsertarMatrizDias.hOidsGruposAnyadir', arrOidsGruposAnyadir);			

                  //obtenemos las descripción multiselección de cbGrupoDeZonas
                  var arrNombresGruposAnyadir = get('frmInsertarMatrizDias.cbGrupoDeZonas','T');
                  set('frmInsertarMatrizDias.hNombresGruposAnyadir', arrNombresGruposAnyadir);

                  //ejecutamos la accion insertaGrupo
                  set('frmInsertarMatrizDias.accion', 'insertaGrupo');
                  enviaSICC('frmInsertarMatrizDias');
         }


         /*
         * Se llama a esta función javascript cuando se pulsa sobre el botón btnAnyadirActividad.
         * Llama a LPMantieneMatrizDias con accion = "insertaActividad"
         */
         function onClickAnyadirActividad() {
                  // validamos campos requeridos
                  if( ! sicc_validaciones_generales('grupoAnyadirActividad') ) {
                           return false;
                  }		

                  //obtenemos los oids multiselección de cbGrupoDeZonasSeleccionados
                  var arrOidsGruposAnyadirMatriz = get('frmInsertarMatrizDias.cbGrupoDeZonasSeleccionados');
                  set('frmInsertarMatrizDias.hOidsGruposAnyadirMatriz', arrOidsGruposAnyadirMatriz);			

                  //obtenemos el oid de la actividad seleccionada en cbActividades
                  var oidActividadAnyadirMatriz = get('frmInsertarMatrizDias.cbActividades');
                  set('frmInsertarMatrizDias.hOidActividadAnyadirMatriz', oidActividadAnyadirMatriz);

                  //ejecutamos la accion insertaGrupo
                  set('frmInsertarMatrizDias.accion', 'insertaActividad');
                  enviaSICC('frmInsertarMatrizDias');
         }


         /*
         * Al seleccionar una actividad en la matriz, guarda su idVista en hActividadSeleccionada.
         * Luego se utiliza este valor para identificar que actividad se desea modificar o eliminar.
         */
        function seleccionaActividad(idVistaActividad) {
                  //tomamos el valor de la actividad seleccionada
                  set('frmInsertarMatrizDias.hActividadSeleccionada', idVistaActividad);
                  //ver el manejo del color al seleccionar la actividad (Incidencia 4460)
                  //debugger;
                  if(objActividadClick!=null){
                           objActividadClick.style.color = objActividadClick.normalColor;
                           objActividadClick.style.fontWeight = "normal";
                  }
                  objActividadClick = event.srcElement;
                  objActividadClick.normalColor = objActividadClick.style.color;
				  objActividadClick.style.color = "#000000";	//incidencia 5505 asi lo define
				  objActividadClick.style.fontWeight = "bold";	//incidencia 5505 asi lo define
         }


         /*
         * Se llama a esta función javascript cuando se pulsa sobre el botón btnEliminar.
         * Llama a LPMantieneMatrizDias con accion = "eliminar"
         */
        function onClickEliminar() {
                  var idVistaActividad = get('frmInsertarMatrizDias.hActividadSeleccionada');
                  // Si seleccionaron una actividad
                  if ( (idVistaActividad != null) && (idVistaActividad != '') ) {
                           //ejecutamos la accion eliminar
                           set('frmInsertarMatrizDias.accion', 'eliminar');
                           enviaSICC('frmInsertarMatrizDias');
                  }
         }


         /*
         * Se llama a esta función javascript cuando se pulsa sobre el botón btnModificar.
         * Llama a LPMantieneMatrizDias con accion = "modificar"
         */
        function onClickModificar() {
                  var idVistaActividad = get('frmInsertarMatrizDias.hActividadSeleccionada');
                  // Si seleccionaron una actividad
                  if ( (idVistaActividad != null) && (idVistaActividad != '') ) {
                           //ejecutamos la accion modificar
                           set('frmInsertarMatrizDias.accion', 'modificar');

                           //muestra ventana modal
                           var parametros = new Object();  
                           parametros.idVistaActividadModificar = idVistaActividad;
                           parametros.varTipo = get('frmInsertarMatrizDias.varTipo');
                           parametros.varModo = get('frmInsertarMatrizDias.varModo');
                           mostrarModalSICC("LPMantieneMatrizDias","modificar", parametros, '650', '250');

                           //actualizamos la matrizDias
                           set('frmInsertarMatrizDias.accion', 'refrescaModificacion');
                           enviaSICC('frmInsertarMatrizDias');
                  }
         }


         /*
         * Se llama a esta función javascript cuando se pulsa sobre el botón btnEliminar 
         * Llama a LPMantieneMatrizDias con accion = "eliminar" pasandole 
         * las actividades seleccionada en la lista de actividades fuera de periodo.
         */
       function onClickEliminarFueraPeriodo() {
                  //tomamos el valor de la actividad seleccionada
                  var idVistaActividad = lstFueraPeriodo.codSeleccionados();
                  set('frmInsertarMatrizDias.hActividadSeleccionada', idVistaActividad);
                  // invocamos a la funcion de eliminar una actividad segun hActividadSeleccionada
                  onClickEliminar();
         }


         /*
         * Se llama a esta función javascript cuando se pulsa sobre el botón btnModificarFueraPeriodo.
         * Llama a LPMantieneMatrizDias con accion = "modificar" pasandole
         * la actividad seleccionada en la lista de actividades fuera de periodo.
         */
