         /*
         * Esta función javascript se realiza cuando se pulsa sobre "Guardar" en el menú. 
         * En diseño es accionGuardar().
         * - Llama a LPMantieneMatrizDias con accion = "guardar"
         */
        function fGuardar() {
                  //actualizamos la matrizDias, en modo oculto NO necesito recarga
                  //eval('frmInsertarMatrizDias').oculto='S';  // en modo oculto NO necesito recarga
                  set('frmInsertarMatrizDias.accion', 'guardar');
                  enviaSICC('frmInsertarMatrizDias');
                  //eval('frmInsertarMatrizDias').oculto='N';

         }


         // Limpia combos
        function fLimpiar() { 
                  //luego de fLimpiar generico, focaliza en el primer combo
                  focaliza('frmInsertarMatrizDias.cbMarcas');
         } 


         /*
         * Este método se activa al seleccionar un valor en cbMarcas.
         */
        function onChangeMarca() {
                  cambiaFiltro();
         }


         /*
         * Este método se activa al seleccionar un valor en cbCanales.
         */
        function onChangeCanal() {
                  cambiaFiltro();
         }


         /*
         * Si existe un valor seleccionado (no vacio) en cmbMarca
         * y existe un valor seleccionado (no vacio) en cmbCanal, habilitar btnBuscar.
         */
        function cambiaFiltro() {
                  var botonBuscar = document.getElementById('btnBuscar');
                  if ( (get('frmInsertarMatrizDias.cbMarcas') != null) && (get('frmInsertarMatrizDias.cbMarcas') != '') &&
                            (get('frmInsertarMatrizDias.cbCanales') != null) && (get('frmInsertarMatrizDias.cbCanales') != '') ) {
                           //habilita el boton buscar
                           botonBuscar.enable = true;
                  } else {
                           //deshabilita el boton buscar
                           botonBuscar.enable = false;
                  }
         }


         // focaliza el cbMarcas si estoy en la capa de busqueda
        function focalizaDesdeBusqueda() {  
                  document.selection.empty();
                  // si no esta visible la capa de seleccion
                  if ( ! get_visibilidad('capaSeleccion') ) {
                           focaliza('frmInsertarMatrizDias.cbMarcas');
                  } else {
                           focaliza('frmInsertarMatrizDias.cbGrupoDeZonas');
                  }
         }


         // focaliza el cbMarcas si estoy en la capa de seleccion
        function focalizaDesdeSeleccion() {  
                  document.selection.empty();
                  // si no estan visibles ninguna de las capas que muestran la matriz
                  if ( ! (get_visibilidad('capaTablaGrafica') || get_visibilidad('capaTablaNoGrafica')) ) {
                           focaliza('frmInsertarMatrizDias.cbMarcas');
                  } else {
                           //druida no se lo banca aca, que raro!!!
                           //focaliza('frmInsertarMatrizDias.btnModificar1');
                           document.all.btnModificar1.focus();
                  }
         }


         // focaliza el cbMarcas desde el boton de cambio de vista
        function focalizaDesdeMatriz() {  
                  document.selection.empty();
                  focaliza('frmInsertarMatrizDias.cbMarcas');
         }


         // focaliza shiftTab segun las capas visibles
        function focalizaShift() {  
                  document.selection.empty();
                  // vemos si esta visible la capaTablaGrafica
                  if (get_visibilidad('capaTablaGrafica')) {
                           //druida no se lo banca aca, que raro!!!
                           //focaliza('frmInsertarMatrizDias.btnModoNoGrafico');
                           document.all.btnModoNoGrafico.focus();
                           return;
                  }
                  // vemos si esta visible la capaTablaNoGrafica
                  if (get_visibilidad('capaTablaNoGrafica')) {
                           //druida no se lo banca aca, que raro!!!
                           //focaliza('frmInsertarMatrizDias.btnModoGrafico');
                           document.all.btnModoGrafico.focus();
                           return;
                  }		
                  // no esta visible la capa que muestra la matriz, vemos si esta visible la capaSeleccion
                  if (get_visibilidad('capaSeleccion')) {
                           //druida no se lo banca aca, que raro!!!
                           //focaliza('frmInsertarMatrizDias.btnAnyadirActividad');
                           document.all.btnAnyadirActividad.focus();
                           return;
                  }
                  // no esta visible la capaSeleccion, solo esta visible la capaBusqueda
                  if (get_visibilidad('capaBusqueda')) {
                           //druida no se lo banca aca, que raro!!!
                           //focaliza('frmInsertarMatrizDias.btnBuscar');
                           document.all.btnBuscar.focus();
                           return;
                  }
         }


         // Levanta los valores seleccionados para marca y canal 
        function obtenerMarcaCanal(){ 
                  set('frmInsertarMatrizDias.hCanal', get('frmInsertarMatrizDias.cbCanales')); 
                  set('frmInsertarMatrizDias.hMarca', get('frmInsertarMatrizDias.cbMarcas')); 
         } 


         //Posicina los combos marca y canal segun el seleccionado
        function posicionarCombo() { 
                  //combo marcas
                  var iSeleccionadoMarca = new Array(); 
                  iSeleccionadoMarca[0] = get('frmInsertarMatrizDias.hMarca'); 
                  set('frmInsertarMatrizDias.cbMarcas',iSeleccionadoMarca); 

                  //combo canales
                  var iSeleccionadoCanal = new Array(); 
                  iSeleccionadoCanal[0] = get('frmInsertarMatrizDias.hCanal'); 
                  set('frmInsertarMatrizDias.cbCanales',iSeleccionadoCanal); 

                  //combo gruposZonas multiple
                  var oidsGruposAnyadir = get('frmInsertarMatrizDias.hOidsGruposAnyadir'); 
                  iSeleccionadoGrupos = oidsGruposAnyadir.split(",");
                  set('frmInsertarMatrizDias.cbGrupoDeZonas',iSeleccionadoGrupos); 

                  //combo grupoZonasSeleccionados multiple
                  var oidsGruposAnyadirMatriz = get('frmInsertarMatrizDias.hOidsGruposAnyadirMatriz'); 
                  iSeleccionadoGruposSelMatriz = oidsGruposAnyadirMatriz.split(",");
                  set('frmInsertarMatrizDias.cbGrupoDeZonasSeleccionados',iSeleccionadoGruposSelMatriz); 

                  //combo activitades
                  var iSeleccionadoActividad = new Array();
                  iSeleccionadoActividad[0] = get('frmInsertarMatrizDias.hOidActividadAnyadirMatriz'); 
                  set('frmInsertarMatrizDias.cbActividades',iSeleccionadoActividad); 
         } 


         //Muestra la lista editable
        function mostrarListaEditable() {
                  //lista editable actividades fuera de periodo
                  document.all["CplstFueraPeriodo"].style.visibility='visible';
                  document.all["CpLin1lstFueraPeriodo"].style.visibility='visible';
                  document.all["CpLin2lstFueraPeriodo"].style.visibility='visible';
                  document.all["CpLin3lstFueraPeriodo"].style.visibility='visible';
                  document.all["CpLin4lstFueraPeriodo"].style.visibility='visible';

                  document.all["primera2Div"].style.visibility='visible';
                  document.all["ret2Div"].style.visibility='visible';
                  document.all["ava2Div"].style.visibility='visible';
                  document.all["separa2Div"].style.visibility='visible';

                  document.all["btnModificarFueraPeriodoDiv"].style.visibility='visible';
                  document.all["btnEliminarFueraPeriodoDiv"].style.visibility='visible';

		  //Otra Campaña
                  document.all["CplstOtraCampana"].style.visibility='visible';
                  document.all["CpLin1lstOtraCampana"].style.visibility='visible';
                  document.all["CpLin2lstOtraCampana"].style.visibility='visible';
                  document.all["CpLin3lstOtraCampana"].style.visibility='visible';
                  document.all["CpLin4lstOtraCampana"].style.visibility='visible';

                  document.all["primera3Div"].style.visibility='visible';
                  document.all["ret3Div"].style.visibility='visible';
                  document.all["ava3Div"].style.visibility='visible';
                  document.all["separa3Div"].style.visibility='visible';

                  document.all["btnModificarOtraCampanaDiv"].style.visibility='visible';
                  document.all["btnEliminarOtraCampanaDiv"].style.visibility='visible';
         }


