var varNoLimpiarSICC = true;

function onLoadPag(){
		cargaCombos();
		configurarMenuSecundario('frmMovimientoBancario'); 
		//deshabilitarHabilitarBoton('botonContenido','btnProcesar','D');
		set("frmMovimientoBancario.boton","D");
		var opcion = get("frmMovimientoBancario.accion");
		accion("frmMovimientoBancario.cbCCC",".disabled=true");
		set("frmMovimientoBancario.cuenta", "S");

		focaliza("frmMovimientoBancario.txtFechaMovimiento");

		if (get('frmMovimientoBancario.errDescripcion')!='') {
				 var wnd = fMostrarMensajeError(get('frmMovimientoBancario.errCodigo'), get('frmMovimientoBancario.errDescripcion'));
		}
}

function cargaCombos(){
	// Combo Horario 
	var opcion1 = GestionarMensaje(1132);
	var opcion2 = GestionarMensaje(1133);

	combo_add('frmMovimientoBancario.cbHorario',"0", opcion2);
	combo_add('frmMovimientoBancario.cbHorario',"1", opcion1);
}

