function cargarLista(){
	//Invocamos a LPInsertarMovimientos pasándole el array de oids (VAR). 
	sicc_cambiar_estado("cbTipoTransaccion",false);   
	sicc_cambiar_estado("txtImportePago",false);   
	sicc_cambiar_estado("txtCodConsultoraReal",false);   
	set("frmMovimientoBancario.procesaLote", "SI");
	var oid = get("frmMovimientoBancario.oids");
	set("frmMovimientoBancario.conectorAction","LPInsertarMovimientos");
	set("frmMovimientoBancario.accion","procesarLote");
	enviaSICC('frmMovimientoBancario');		

}

function buscarCliente(){	
    var obj = new Object();
    var whnd = mostrarModalSICC('LPBusquedaRapidaCliente','',obj);

    if(whnd==null){
         }else{
				var oid = whnd[0];
                var cod = whnd[1];
                set("frmMovimientoBancario.txtCodConsultoraReal", cod);
				set("frmMovimientoBancario.oids", oid);
         }
}

