function fLimpiar(){
	//-------------------------- Datos Cabecera ------------------------------//
	set("frmMovimientoBancario.txtFechaMovimiento", "");	
	set("frmMovimientoBancario.cbEmpresa", "");
	var aCombo = new Array(new Array("",""));
    set_combo("frmMovimientoBancario.cbCCC",aCombo, []);
	//------------------------------------------------------------------------//
	//set("frmMovimientoBancario.lbldtConsecutivo", "1");
	set("frmMovimientoBancario.cbTipoTransaccion", "");
	set("frmMovimientoBancario.txtImportePago", "");
	set("frmMovimientoBancario.txtCodConsultoraReal", "");
	set("frmMovimientoBancario.txtNFacturaBoleta", "");
	set("frmMovimientoBancario.txtDigitoChequeo", ""); 
	set("frmMovimientoBancario.txtNCupon", "");
	set("frmMovimientoBancario.txtOficina", "");
	set("frmMovimientoBancario.txtNombreOficina", "");
	set("frmMovimientoBancario.cbHorario", "");
	set("frmMovimientoBancario.areatxtObservaciones", "");
	set("frmMovimientoBancario.oids","");	

	setMV("frmMovimientoBancario.txtFechaMovimiento", "c");
	setMV("frmMovimientoBancario.cbEmpresa", "c");
	setMV("frmMovimientoBancario.cbTipoTransaccion", "c");
	setMV("frmMovimientoBancario.cbCCC", "c");
	setMV("frmMovimientoBancario.txtImportePago", "c");
	setMV("frmMovimientoBancario.txtCodConsultoraReal", "c");
	set("frmMovimientoBancario.consecutivo", 1);

	//--- Habilitacion de los campos y deshabilita el btn Procesar Lote -----//

	accion("frmMovimientoBancario.txtFechaMovimiento",".disabled=false");
	accion("frmMovimientoBancario.cbEmpresa", ".disabled=false");
	//deshabilitarHabilitarBoton('botonContenido','btnProcesar','D');	
	set("frmMovimientoBancario.boton", "D");

	focaliza("frmMovimientoBancario.txtFechaMovimiento"); 
	accion("frmMovimientoBancario.cbCCC",".disabled=true");
	set("frmMovimientoBancario.cuenta", "S");

	set("frmMovimientoBancario.conectorAction","LPInsertarMovimientos");
	set("frmMovimientoBancario.accion","limpiar");
	enviaSICC('frmMovimientoBancario');
}

function fGuardar(){	

 var sepMiles = get("formulario.hSepMiles");
 var sepDec = get("formulario.hSepDec");

 var formatoFecha = get("frmMovimientoBancario.hFormatoFechaPais" );
 var fec = get("frmMovimientoBancario.txtFechaMovimiento");

	if (!EsFechaValida_SICC(fec , "frmMovimientoBancario") ){
			GestionarMensaje("1006", formatoFecha, null, null);
			focaliza("frmMovimientoBancario.txtFechaMovimiento");
			return false;
		}else
				var OK = true;

	if (sicc_validaciones_generales() && (OK) ){
		//set("frmMovimientoBancario.oids", oid);
		set("frmMovimientoBancario.conectorAction","LPInsertarMovimientos");
		set("frmMovimientoBancario.accion","guardar");
		enviaSICC('frmMovimientoBancario');		
	}	
}



function empresaOnChange(){ 
	var arr = new Array();
	var emp = get('frmMovimientoBancario.cbEmpresa');
	set('frmMovimientoBancario.oids' + emp);
    arr[arr.length]=new Array("oid", emp);	
	accion("frmMovimientoBancario.cbCCC",".disabled=false");
	set("frmMovimientoBancario.cuenta", "N");
	recargaCombo('frmMovimientoBancario.cbCCC', 'CCCRecargarCCEmpresa', 'es.indra.sicc.util.DTOOID', arr);	
} 

function errorAlGuardar(){ 	
	var ok = cdos_mostrarConfirm(GestionarMensaje("UICCC0037"));
		if(!ok){
			set("frmMovimientoBancario.conectorAction","LPInsertarMovimientos");
		   set("frmMovimientoBancario.accion","");
			eval('frmMovimientoBancario').oculto="N";
		   enviaSICC('frmMovimientoBancario');			
		}else{
			set("frmMovimientoBancario.flag", "graba");
			fGuardar();
		}
		//focaliza("frmMovimientoBancario.txtCodConsultoraReal"); 	

}



