function seteaValores(valorConse, oidMovimiento){
		//alert("recibo: "+oidMovimiento);
		set("frmMovimientoBancario.ventanaOculta", "S");
		accion('frmMovimientoBancario.cbEmpresa', '.disabled=true'); 
		accion('frmMovimientoBancario.txtFechaMovimiento','.disabled=true');
		accion('frmMovimientoBancario.cbCCC','.disabled=true');		
		set("frmMovimientoBancario.consecutivo" , valorConse);
		var oid = get("frmMovimientoBancario.procesosOids") + "," ;

		//alert(' oids >>>> ' + get("frmMovimientoBancario.oids"));
		set("frmMovimientoBancario.concatena" , oidMovimiento);
		set("frmMovimientoBancario.cad" , oidMovimiento);
		set("frmMovimientoBancario.boton", "A");
		txt_to('lbldtConsecutivo', valorConse);
		set("frmMovimientoBancario.cbTipoTransaccion", "");
		set("frmMovimientoBancario.txtImportePago", "");
		set("frmMovimientoBancario.txtCodConsultoraReal", "");
		set("frmMovimientoBancario.txtNFacturaBoleta", "");
		set("frmMovimientoBancario.txtDigitoChequeo", ""); 
		set("frmMovimientoBancario.txtNCupon", "");
		set("frmMovimientoBancario.txtOficina", "");
		set("frmMovimientoBancario.txtNombreOficina", "");
		set("frmMovimientoBancario.cbHorario", "");
		set("frmMovimientoBancario.areatxtObservaciones", "");
		set("frmMovimientoBancario.flag", "");
		focaliza("frmMovimientoBancario.cbTipoTransaccion");
		//deshabilitarHabilitarBoton('botonContenido','btnProcesar','A');	 		
		modificarOid();
}


