function muestraLista(ultima, rowset) { 
  var tamanio = rowset.length;
  if (tamanio > 0) { 
			mostrarLista();
			mipgndo.lista.maxSel = -1;
			return true;
 }
 else {
			ocultarLista();
			return false;
		}
}

