PAGINA.JAVASCRIPT:


function onLoadPag(){   
   //Se configura el menu secundario
   configurarMenuSecundario("formulario");
   btnProxy(2, 0);    
   cargaDatos();

    var campos = get("formulario.deshaCampos");  
    if(campos == "si"){
         deshabilitaCamposA();
    }   

    var camposCK = get("formulario.deshaCamposCheck");    
    if(camposCK == "si"){
         deshabilitaCamposB();
    }    

    var act = get("formulario.ckActividad");    
    if (act =="S"){     
         accion("formulario.txtMontoMinPedidoHijas1", ".disabled=false");
         accion("formulario.txtUnidadesMinPedidoHijas1", ".disabled=false");    
    }    

    var camposCKP = get("formulario.deshaCamposCheckPago");     
    if(camposCKP == "si"){
         habilitaCamposC(); 
    }
         else{           
         deshabilitaCamposC();
         }	

         var menu = get("formulario.opcionMenu");  

         if (menu == "Modificar Concurso"){
                  btnProxy(2,'1');
                  btnProxy(3,'0');
                  btnProxy(7,'0');
                  btnProxy(8,'0');
                  btnProxy(9,'0');
         }

         if (menu == "Consultar Concurso"){
      deshabilitaCamposD();      
      btnProxy(1, 0);
      btnProxy(2, 1);
      btnProxy(3, 0);
      btnProxy(5, 0);      
      btnProxy(7, 0);
      btnProxy(8, 0);
      btnProxy(9, 0);
         }
         else{  
                  onChangeActividad();
                  onChangePagoTiempo();
                  focaliza("formulario.cbPeriodoDesde");
         }

         finCargaPagina = true;              
 }

 function fVolver() {
         window.close();
}

function habilitaCamposC(){
         accion("formulario.cbPeriodoDesdePagoTiempo", ".disabled=false");
    accion("formulario.cbPeriodoHastaPagoTiempo", ".disabled=false");
    accion("formulario.txtDiasGracia", ".disabled=false");
}

function cargaDatos(){      
           //1° Periodo desde
      set("formulario.cbPeriodoDesde", new Array( get("formulario.TempcbPeriodoDesde") ));

           //2° Periodo hasta
      set("formulario.cbPeriodoHasta", new Array(get("formulario.TempcbPeriodoHasta")));

           //3° Periodos para calculo      
      set("formulario.txtPeriodoEsperaParaCalculo", get("formulario.TemptxtPeriodoEsperaParaCalculo"));

           //4° Periodos sobre calculo
      set("formulario.txtPeriodoEsperaSobreCalculo", get("formulario.TemptxtPeriodoEsperaSobreCalculo"));

           //5° Numero minimo recomendadas      
      set("formulario.txtNumMinRecomendadas", get("formulario.TemptxtNumMinRecomendadas"));      

           //6° Monto minomo red
      set("formulario.txtMontoMinRed", get("formulario.TemptxtMontoMinRed"));

           //7° Pedidos minimo red      
      set("formulario.txtPedidoMinRed", get("formulario.TemptxtPedidoMinRed"));

           //8° Unidades min red
      set("formulario.txtUnidadesMinRed", get("formulario.TemptxtUnidadesMinRed"));

           //9° Monto minimo madre      
      set("formulario.txtMontoMinMadre", get("formulario.TemptxtMontoMinMadre"));   

           //10° Pedidos minimos madre
      set("formulario.txtPedidoMinMadre", get("formulario.TemptxtPedidoMinMadre"));

           //11° Unidades min madre
      set("formulario.txtUnidadesMinMadre", get("formulario.TemptxtUnidadesMinMadre"));    

           //12° Monto min pedido por hijas     
      set("formulario.txtMontoMinPedidoHijas",get("formulario.TemptxtMontoMinPedidoHijas"));

           //13° Pedidos minimos hijas
      set("formulario.txtPedidoMinHijas", get("formulario.TemptxtPedidoMinHijas"));   

           //14° Unidades min hijas
           set("formulario.txtUnidadesMinPedidoHijas",get("formulario.TemptxtUnidadesMinPedidoHijas"));      

           //15° Calculo premio
           set("formulario.cbCalculoPremio", new Array(get("formulario.TempcbCalculoPremio")) );

           //16° Porcentaje sobre ventas
           set("formulario.txtPorcentajePremioSobreVenta", get("formulario.TemptxtPorcentajePremioSobreVenta"));

           //17° Porcentaje sobre cobranza
      set("formulario.txtPorcentajePremioSobreCobranza", get("formulario.TemptxtPorcentajePremioSobreCobranza"));

           //18° Actividad
           var actividad = get("formulario.TempckActividad");
      if(actividad == "true"){
           set("formulario.ckActividad", "S");
      }

           //19° Monto minimo pedido hijas
           set("formulario.txtMontoMinPedidoHijas1",get("formulario.TemptxtMontoMinPedidoHijas1"));      

           //20° Unidades min pedido hijas
      set("formulario.txtUnidadesMinPedidoHijas1",get("formulario.TemptxtUnidadesMinPedidoHijas1"));      

           //21° Pago a tiempo
           var pago = get("formulario.TempckPagoTiempo");
      if(pago == "true"){
           set("formulario.ckPagoTiempo", "S");
      }

           //22° Periodo desde pago	  
      set("formulario.cbPeriodoDesdePagoTiempo", new Array(get("formulario.TempcbPeriodoDesdePagoTiempo")) );

           //23° Periodo hasta pago
           set("formulario.cbPeriodoHastaPagoTiempo", new Array(get("formulario.TempcbPeriodoHastaPagoTiempo")) );

           //24° Dias gracia	         
      set("formulario.txtDiasGracia", get("formulario.TemptxtDiasGracia"));           

           //25° Oid
           set("formulario.oid", get("formulario.oid"));           
}

function deshabilitaCamposA(){  
