    accion("formulario.txtMontoMinPedidoHijas1", ".disabled=true");
    accion("formulario.txtUnidadesMinPedidoHijas1", ".disabled=true");
    accion("formulario.cbPeriodoDesdePagoTiempo", ".disabled=true");
    accion("formulario.cbPeriodoHastaPagoTiempo", ".disabled=true");
    accion("formulario.txtDiasGracia", ".disabled=true");  
} 

function deshabilitaCamposB(){
    accion("formulario.txtMontoMinPedidoHijas1", ".disabled=false");
