function focalizaShTab(){
         focaliza("formulario.txtDiasGracia");  
}

function  focalizaTab(){
         focaliza("formulario.cbPeriodoDesde");
}  

function onChangeActividad(){      
         var uno = get("formulario.ckActividad");  
         if(uno == "S"){
                  accion("formulario.txtMontoMinPedidoHijas1", ".disabled=false");
         accion("formulario.txtUnidadesMinPedidoHijas1", ".disabled=false");
    }
         else{            
             var monMinPedHijas = get("formulario.txtMontoMinPedidoHijas1");

         if (monMinPedHijas != ""){
                      set("formulario.txtMontoMinPedidoHijas1", "");             
         }            

                  accion("formulario.txtMontoMinPedidoHijas1", ".disabled=true");

      var monUMinPedHijas = get("formulario.txtUnidadesMinPedidoHijas1");

         if (monUMinPedHijas !=""){
                           set("formulario.txtUnidadesMinPedidoHijas1", "");    
         }                   

         accion("formulario.txtUnidadesMinPedidoHijas1", ".disabled=true"); 
    }
} 

function onChangePagoTiempo(){        
         var dos = get("formulario.ckPagoTiempo");      
    if (dos == "S"){                
             accion("formulario.cbPeriodoDesdePagoTiempo", ".disabled=false");
         accion("formulario.cbPeriodoHastaPagoTiempo", ".disabled=false");
         accion("formulario.txtDiasGracia", ".disabled=false");      
    }
         else{ 	
                  var arr = new Array();

                  var cbPeriodoDesdePagoTiempo = get("formulario.cbPeriodoDesdePagoTiempo");
                  var cbPeriodoHastaPagoTiempo = get("formulario.cbPeriodoHastaPagoTiempo");

                  if(cbPeriodoDesdePagoTiempo != ""){

                           set("formulario.cbPeriodoDesdePagoTiempo", arr);
                  }

                  if(cbPeriodoHastaPagoTiempo != ""){

                           set("formulario.cbPeriodoHastaPagoTiempo", arr);
                  }

                           set("formulario.txtDiasGracia", "");	
                    accion("formulario.cbPeriodoDesdePagoTiempo", ".disabled=true");
         accion("formulario.cbPeriodoHastaPagoTiempo", ".disabled=true");           
         accion("formulario.txtDiasGracia", ".disabled=true");           
         set("formulario.deshaCamposCheckPago", "si");
    }    
}  

function comprobarDatosObligatorios(){
         //Si se ingresan todos los datos obligatorios
         if((get("formulario.cbPeriodoDesde")!="") && 
            (get("formulario.cbPeriodoHasta")!="") &&
            (get("formulario.txtPeriodoEsperaParaCalculo")!="") && 
            (get("formulario.txtPeriodoEsperaSobreCalculo")!="") &&
            (get("formulario.txtNumMinRecomendadas")!="") && 
            (get("formulario.cbCalculoPremio")!="")) {	

            //Si se chequea pago tiempo
            if(get("formulario.ckPagoTiempo")=="S") {

                           //Si se ingresa un valor para periodo pago desde y dias gracia
                           if((get("formulario.cbPeriodoDesdePagoTiempo")!="") && 
                                  (get("formulario.txtDiasGracia")!="")) {

                                    //Si se chequea actividad
                                    if(get("formulario.ckActividad")=="S") {

                                             //Si se ingresa valor para monto minimo pedido hijas
                                             //y unidades mini pedido hijas, devuelve true
                                             if((get("formulario.txtMontoMinPedidoHijas1")!="") && 		     (get("formulario.txtUnidadesMinPedidoHijas1")!="")) {
                                                      return true;
                                             }
                                             //Si NO se ingresa valor para monto minimo pedidio hijas
                                             //y unidades mini pedido hijas, devuelve false
                                             else {
                                                      return false;
                                             }
                                    }
                                    //Si NO se chequea actividad devuelve true
                                    else {
                                             return true;
                                    }
                           }
                           //Si NO se ingresa un valor para periodo pago desde y dias gracia 
                           //devuele false
                           else {
                                    return false;
                           }
            }
            //Si NO se chequea pago tiempo
            else {
                           //Si se chequea actividad
                           if(get("formulario.ckActividad")=="S") {

                                    //Si se ingresa un valor para monto min pedido hijas
                                    //y unidades min pedido hijas, devuelve true
                                    if((get("formulario.txtMontoMinPedidoHijas1")!="") && 		     (get("formulario.txtUnidadesMinPedidoHijas1")!="")) {
                                                      return true;
                                    }
                                    //Si NO se ingresa un valor para monto min pedido hijas
                                    //y unidades min pedido hijas, devuelve false
                                    else {
                                             return false;
                                    }
                           }
                           //Si NO se chequea actividad
                           else {
                                    return true;
                           }	
            }
         }	
         //Si NO se ingresan todos los datos obligatorios devuelve false
         else {
                  return false;
         }
} 

function fGuardar(){ 
         //Agregado por dmaneiro - 01/12/2005 - inc. 21877
         var ckActividad =  get("formulario.ckActividad");
         var ckPagoTiempo = get("formulario.ckPagoTiempo");    

         //Se validan los campos numericos
         if(!sicc_validaciones_generales('validaNumeros')) {
                  return;
         }	

         if(!sicc_validaciones_generales('validaTexto')){
                  return;	
         }

         if (ckActividad == "S"){     
                           if(!sicc_validaciones_generales('segunActividad')) {
                                             return;
                           }	 
          }  

         if (ckPagoTiempo == "S"){     
                           if(!sicc_validaciones_generales('segunPagoTiempo')) {
                                             return;
                           }	 
          }  	

         //Si paso las validaciones, toma los valores de los campos
         //de la pantalla
         tomarValoresCamposPantalla();

         //Se realiza el envia oculto para guardar
         eval("formulario").oculto= "S";
    set("formulario.conectorAction","LPMantenerMultinivel");
    set("formulario.accion","guardar");         
    enviaSICC('formulario','','','N');   
} 

function fLimpiar(){
         //Deja la pantalla como cuando ingreso (Requisitos generales)
         onLoadPag();
}

function onClickPestanyaPaginaMultinivel(valor, form, nombreLP) {
   //Agregado por dmaneiro - 01/12/2005 - inc. 21877
         var ckActividad =  get("formulario.ckActividad");
         var ckPagoTiempo = get("formulario.ckPagoTiempo");      

         //1° Al hacer click en otra pestaña, valida los campos numericos
         if(!sicc_validaciones_generales('validaNumeros')) {
                  return;
         }

         //Agregado por dmaneiro - 01/12/2005 - inc. 21877
         if(!sicc_validaciones_generales('validaTexto')){
                  return;	
         }

         if (ckActividad == "S"){     
                           if(!sicc_validaciones_generales('segunActividad')) {
                                             return;
                           }	 
          }  

         if (ckPagoTiempo == "S"){     
                           if(!sicc_validaciones_generales('segunPagoTiempo')) {
                                             return;
                           }	 
          }  	

         //2° Si pasa las validaciones, toma los valores de los campos
         //de la pantalla y redirige a la otra pestaña
         tomarValoresCamposPantalla();	
         onClickPestanyaHabilitada(valor, form, nombreLP);
}

function tomarValoresCamposPantalla() {
         //Se toman todos los valores de la pantalla
         var TempcbPeriodoDesde = get("formulario.cbPeriodoDesde");
         var TempcbPeriodoHasta = get("formulario.cbPeriodoHasta");
         var TemptxtPeriodoEsperaParaCalculo = get("formulario.txtPeriodoEsperaParaCalculo");
         var TemptxtPeriodoEsperaSobreCalculo = get("formulario.txtPeriodoEsperaSobreCalculo");
         var TemptxtNumMinRecomendadas = get("formulario.txtNumMinRecomendadas");
         var TemptxtMontoMinRed = get("formulario.txtMontoMinRed");
         var TemptxtPedidoMinRed = get("formulario.txtPedidoMinRed");
         var TemptxtUnidadesMinRed = get("formulario.txtUnidadesMinRed");
         var TemptxtMontoMinMadre = get("formulario.txtMontoMinMadre");
         var TemptxtPedidoMinMadre = get("formulario.txtPedidoMinMadre");
         var TemptxtUnidadesMinMadre = get("formulario.txtUnidadesMinMadre");
         var TemptxtMontoMinPedidoHijas = get("formulario.txtMontoMinPedidoHijas");
         var TemptxtPedidoMinHijas = get("formulario.txtPedidoMinHijas");
         var TemptxtUnidadesMinPedidoHijas = get("formulario.txtUnidadesMinPedidoHijas");
         var TempcbCalculoPremio = get("formulario.cbCalculoPremio");
         var TemptxtPorcentajePremioSobreVenta = get("formulario.txtPorcentajePremioSobreVenta");
         var TemptxtPorcentajePremioSobreCobranza = get("formulario.txtPorcentajePremioSobreCobranza");
         var TempckActividad = get("formulario.ckActividad");
         var TemptxtMontoMinPedidoHijas1 = get("formulario.txtMontoMinPedidoHijas1");
         var TemptxtUnidadesMinPedidoHijas1 = get("formulario.txtUnidadesMinPedidoHijas1");
         var TempckPagoTiempo = get("formulario.ckPagoTiempo");
         var TempcbPeriodoDesdePagoTiempo = get("formulario.cbPeriodoDesdePagoTiempo");
         var TempcbPeriodoHastaPagoTiempo = get("formulario.cbPeriodoHastaPagoTiempo");
         var TemptxtDiasGracia = get("formulario.txtDiasGracia");

         var datosObligatorios = comprobarDatosObligatorios();
         var opcionMenu = get("formulario.opcionMenu");

         set("formulario.TempcbPeriodoDesde", TempcbPeriodoDesde);
         set("formulario.TempcbPeriodoHasta", TempcbPeriodoHasta);
         set("formulario.TemptxtPeriodoEsperaParaCalculo", TemptxtPeriodoEsperaParaCalculo);
         set("formulario.TemptxtPeriodoEsperaSobreCalculo", TemptxtPeriodoEsperaSobreCalculo);
         set("formulario.TemptxtNumMinRecomendadas", TemptxtNumMinRecomendadas);
         set("formulario.TemptxtMontoMinRed", TemptxtMontoMinRed);
         set("formulario.TemptxtPedidoMinRed", TemptxtPedidoMinRed);
         set("formulario.TemptxtUnidadesMinRed", TemptxtUnidadesMinRed);
         set("formulario.TemptxtMontoMinMadre", TemptxtMontoMinMadre);
         set("formulario.TemptxtPedidoMinMadre", TemptxtPedidoMinMadre);
         set("formulario.TemptxtUnidadesMinMadre", TemptxtUnidadesMinMadre);
         set("formulario.TemptxtMontoMinPedidoHijas", TemptxtMontoMinPedidoHijas);
         set("formulario.TemptxtPedidoMinHijas", TemptxtPedidoMinHijas);
         set("formulario.TemptxtUnidadesMinPedidoHijas", TemptxtUnidadesMinPedidoHijas);
         set("formulario.TempcbCalculoPremio", TempcbCalculoPremio);
         set("formulario.TemptxtPorcentajePremioSobreVenta", TemptxtPorcentajePremioSobreVenta);
         set("formulario.TemptxtPorcentajePremioSobreCobranza", TemptxtPorcentajePremioSobreCobranza);
         set("formulario.TempckActividad", TempckActividad);
         set("formulario.TemptxtMontoMinPedidoHijas1", TemptxtMontoMinPedidoHijas1);
         set("formulario.TemptxtUnidadesMinPedidoHijas1", TemptxtUnidadesMinPedidoHijas1);
         set("formulario.TempckPagoTiempo", TempckPagoTiempo);
         set("formulario.TempcbPeriodoDesdePagoTiempo", TempcbPeriodoDesdePagoTiempo);
         set("formulario.TempcbPeriodoHastaPagoTiempo", TempcbPeriodoHastaPagoTiempo);
         set("formulario.TemptxtDiasGracia", TemptxtDiasGracia);

         set("formulario.datosObligatorios", datosObligatorios);
         set("formulario.opcionMenu", opcionMenu);
}

function onShTabPeriodoDesde() {
         if(get("formulario.ckPagoTiempo")=="S") {
                  focaliza("formulario.txtDiasGracia");
         }
         else {
                  focaliza("formulario.ckPagoTiempo");
         }
}

function OntabPagoTiempo() {
         if(document.getElementById("cbPeriodoDesdePagoTiempo").disabled == true) {
                  focaliza("formulario.cbPeriodoDesde");
         }
         else {
                  focaliza("formulario.cbPeriodoDesdePagoTiempo");
         }	
}
