PAGINA.JAVASCRIPT:


var vConstantesPestInc = new Array();           

function fLimpiar() {
          onLoadPag();
}

function onLoadPag() {  
          configurarMenuSecundario('formulario');
    set("formulario.ckReingresos", get("formulario.tempCKReingresos"));

    if (get('formulario.oidPlantillaBaseCalculo') != ''){
              marcaSeleccionDelUsuario('formulario.cbBaseCalculo', get('formulario.oidPlantillaBaseCalculo'));              
              onChangeBaseCalculo();
                    accion("formulario.cbBaseCalculo",".disabled=true");

          if (  get('formulario.oidPlantillaBaseCalculo') == get('formulario.oidBC_Recomendada')){
                              marcaSeleccionDelUsuario('formulario.cbEfectividad', get('formulario.hidEfectividad'));
              document.getElementById("capa1").style.visibility='visible';

                            // vbongiov -- Cambio 20080580 -- 13/8/2008
                            accion('formulario.cbConcursoPuntajeRecomendada', '.disabled=true');			

          }
          else{
                              document.getElementById("capa1").style.visibility='hidden';
          }
    }
    else
              document.getElementById("capa1").style.visibility='hidden';

    if (get('formulario.ocultaParticipante') == '1'){
                    deshabilitarHabilitarBoton('botonContenido','btnParticipantes','D');
    }


    if (get('formulario.hidIndConsultoras') == '1'){ //Ya habia ingresado antes
          if (get('formulario.oidBaseCalculo') !=''){
                         marcaSeleccionDelUsuario('formulario.cbBaseCalculo', get('formulario.oidBaseCalculo'));

                              if (  get('formulario.oidBaseCalculo') == get('formulario.oidBC_Recomendada')){
                                         marcaSeleccionDelUsuario('formulario.cbEfectividad', get('formulario.hidEfectividad'));
                    marcaSeleccionDelUsuario('formulario.cbPeriodoInicialEvaluacion', 
                    get('formulario.hidPeriodoInicialEval'));

                                      // vbongiov -- Cambio 20080580 -- 13/8/2008
                                      if(get('formulario.indGenerarPuntajeARecomendadas')=="S"){
                                             accion('formulario.cbConcursoPuntajeRecomendada', '.disabled=false');
                                                      marcaSeleccionDelUsuario('formulario.cbConcursoPuntajeRecomendada', get('formulario.hidOidConcursoPuntajeRecomendada'));
                                      } else{
                                                      accion('formulario.cbConcursoPuntajeRecomendada', '.disabled=true');
                                      }

                    // Muestra la capa Recomandadas
                    document.getElementById("capa1").style.visibility='visible';     
              }
              else
                                         document.getElementById("capa1").style.visibility='hidden';
          }
     }

	 if ( get('formulario.opcionMenu') == 'Consultar Concurso'){
                    deshabilitaCampos();
          btnProxy(1, 0);
          btnProxy(2, 1);
          btnProxy(3, 0);
          btnProxy(5, 0);      
          btnProxy(7, 0);
          btnProxy(8, 0);
          btnProxy(9, 0);
    }
    else if(get('formulario.opcionMenu') == 'Modificar Concurso') {
              btnProxy(2,'1');
          btnProxy(3,'0');
          btnProxy(7,'0');
          btnProxy(8,'0');
                    btnProxy(9,'0');
    }

     var vConst = get('formulario.hidConstantesPestINC');
     vConstantesPestInc = vConst.split("|");           

     //Deja seleccionado el valor del combo Tipo venta, si es que tiene
           if(get("formulario.oidTipoVenta")!="") {
                    marcaSeleccionDelUsuario('formulario.cbTipoVenta', get("formulario.oidTipoVenta"));
           }     

     focaliza('formulario.txtMontoMinimo');
     finCargaPagina = true;
}    

function fVolver() {
          window.close();
}

function accionExitosa(){
          eval('formulario').oculto="N";
}

    function onClickEstatusVenta (){
           var obj = new Object();
           obj.opcionMenu = get('formulario.opcionMenu');

           var whnd = mostrarModalSICC('LPMantenerParametrosConsultoras','cargar estatus venta',obj,null,510);
           if (whnd != null){
                set('formulario.hidIngresoEVTA','1');
                set ('formulario.hidLstEstatusVta',whnd[0]);
                set('formulario.conectorAction', 'LPMantenerParametrosConsultoras');
                eval('formulario').oculto="S"; 
                set('formulario.accion', 'almacenar estatus venta');
                enviaSICC('formulario');
           }
    }

    function onClickMontoVentaRecomendada () {           
           var obj = new Object();
           obj.opcionMenu = get('formulario.opcionMenu');
           var whnd = mostrarModalSICC('LPMantenerParametrosConsultoras','cargar monto venta recomendadas',obj,null,260);
           if (whnd != null){
                set('formulario.hidIngresoMVR','1');
                set('formulario.hidLstMVR', whnd[0]) ;
                                  //Cleal
                                  set('formulario.hidCargoAlMenosUno', whnd[1]) ;

                set('formulario.conectorAction', 'LPMantenerParametrosConsultoras');
                eval('formulario').oculto="S"; 
                set('formulario.accion', 'almacenar monto venta recomendadas');
                enviaSICC('formulario');
           }
    }

    function onClickParticipante() {                      
           var obj = new Object();
           obj.opcionMenu = get('formulario.opcionMenu');
           var whnd = mostrarModalSICC('LPMantenerParametrosConsultoras','cargar clasificacion participantes',obj);
           if (whnd != null) {                     
                set('formulario.hidIngresoPAR','1');
                set('formulario.hidLstClasConsul', whnd[0] );
                set('formulario.hidListaClasif', whnd[1] );

                set('formulario.conectorAction', 'LPMantenerParametrosConsultoras');
                eval('formulario').oculto="S"; 
                set('formulario.accion', 'almacenar Clasificacion Participantes');
                enviaSICC('formulario', null, null, 'N');   // Modificado por ssantana, 21/6/2005 - Ahora no valida Req="S"
           }
    }

          function onChangeBaseCalculo () {
                    var vBC = get('formulario.cbBaseCalculo','V');

          if (vBC == get('formulario.oidBC_Recomendada')) {
                    document.getElementById("capa1").style.visibility='visible';              
              accion("formulario.cbTipoVenta",".disabled=true");
          }
          else{
                              document.getElementById("capa1").style.visibility='hidden';
              limpiaCapaRecomendados();

              if(vBC == get('formulario.oidBC_Monto')) {
                                         var valor = new Array();
                    set("formulario.cbTipoVenta", valor);
                    accion("formulario.cbTipoVenta",".disabled=false");
              }
              else {
                    accion("formulario.cbTipoVenta",".disabled=true");
              }              
          }
    }

    function limpiaCapaRecomendados(){
           set('formulario.RecomendacionEfectiva', 'N');
           set('formulario.cbEfectividad','');

           set('formulario.cbPeriodoInicialEvaluacion', '');
           set('formulario.txtNumPeriodosEvaluar','');
           set('formulario.txtNumMinPedidos','');
           set('formulario.txtNumMinPedidosRecomendadas','');
           // vbongiov -- 17/03/2008 -- Cambio RI 20080535
           set('formulario.indPremioCampEfect', 'N');
                    onChangeRecomendacionEfectiva();

                    // vbongiov -- Cambio 20080580 -- 13/8/2008
                    set('formulario.indGenerarPuntajeARecomendadas', 'N');
                    onChangeGenerarPuntaj();

    }

    function onChangeRecomendacionEfectiva(){
           if(get("formulario.RecomendacionEfectiva")=="S")
                accion('formulario.cbEfectividad','.disabled=false');
           else
                accion('formulario.cbEfectividad','.disabled=true');
    }

          function accionGuardar(){
                    //Se valida los campos numericos Monto minimo pedido y Unidades minimas pedido
          //Cleal - 21049

                    if(!sicc_validaciones_generales('datosAdicionalesObligatorios') || !sicc_validaciones_generales('datosAdicionales')) {
                     return;
          }

                    if(get('formulario.cbBaseCalculo') == get('formulario.oidBC_Monto')) {
                     if(!sicc_validaciones_generales('datosAdicionalesObligatoriosTipoVenta')) {
                              return;
                     }     
                    }              


          if(get_visibilidad('capa1')) {
                              //Se validan los campos Numeros periodos evaluar, minimo pedidos y minimo pedidos 
              //recomendada
              if(!sicc_validaciones_generales('recomendadas')) {
                                         return;
              }


                            // vbongiov -- Cambio 20080580 -- 13/8/2008
                             if(get('formulario.indGenerarPuntajeARecomendadas')=="S"){
                                    if(!sicc_validaciones_generales("recomendadas1")){
                                             return;
                                    }
                             }
          }

          tomaValoresPantalla();

          set('formulario.conectorAction', 'LPMantenerParametrosConsultoras');
          eval('formulario').oculto="S"; 
          set('formulario.accion', 'guardar');
          enviaSICC('formulario','','','N');
    }

           function tomaValoresPantalla(){
                      //Se pasa a la LP el oid de de parametros consultoras
           set('formulario.oidParamConsultoras',get('formulario.oidParamConsultoras'));

                      //Se setea la variable datosObligatorios
                      var vDatosOblig = comprobarDatosObligatorios();
           set('formulario.datosObligatorios', vDatosOblig);

                      // Se setean los valores de Datos adicionales
           set('formulario.hidMontoMinimoPedido',get('formulario.txtMontoMinimo'));
           set('formulario.hidUnidadesMinimasPedido',get('formulario.txtUnidadesMinimas'));
           set('formulario.hidReingresosPierdenPuntaje',get('formulario.ckReingresos'));
           set('formulario.oidBaseCalculo', get('formulario.cbBaseCalculo','V'));
                      set('formulario.oidTipoVenta', get('formulario.cbTipoVenta','V'));

                      // Se setean los valores de Recomendadas           
           set('formulario.hidRecomendacionEfectiva',get('formulario.RecomendacionEfectiva'));
           set('formulario.hidEfectividad',get('formulario.cbEfectividad','V'));
           set('formulario.hidPeriodoInicialEval',get('formulario.cbPeriodoInicialEvaluacion','V'));
           set('formulario.hidNroPeriodosEvaluar',get('formulario.txtNumPeriodosEvaluar'));
           set('formulario.hidNroMinimoPedidos',get('formulario.txtNumMinPedidos'));
           set('formulario.hidNroMinPedidosReco',get('formulario.txtNumMinPedidosRecomendadas'));  
           // vbongiov -- 17/03/2008 -- Cambio RI 20080535
           set('formulario.hidIndPremioCampEfect',get('formulario.indPremioCampEfect'));  

                    // vbongiov -- Cambio 20080580 -- 13/8/2008
                    set('formulario.hidIndGenerarPuntajeARecomendadas',get('formulario.indGenerarPuntajeARecomendadas')); 
                    set('formulario.hidOidConcursoPuntajeRecomendada',get('formulario.cbConcursoPuntajeRecomendada')); 

                      set('formulario.accion',get('formulario.accion'));
           set('formulario.opcionMenu',get('formulario.opcionMenu'));      

           /*if (get('formulario.cbBaseCalculo','V') == get('formulario.oidBC_Recomendada')){
                if( get('formulario.hidLstMVR') == ''){
                      GestionarMensaje('1499');
                      return false;
                }
           }*/
    }

function comprobarDatosObligatorios() {
                     if(get('formulario.cbBaseCalculo') == '') {
                              return false;
           }
           else {
                                 if(get('formulario.cbBaseCalculo') == get('formulario.oidBC_Monto')) {
                                          if(get('formulario.cbTipoVenta') == '') {
                                                    return false;
                                          }   
                                          else {
                                                   return true;
                                          }
                                 }                     
                                 else {
                              return true;
                                 }
           }
}

function onClickPestanyaConsultoras(valor) {
           //1° Al seleccionar otra pestaña, primero se valida los campos numericos Monto minimo pedido
           //y Unidades minimas pedido
           //Cleal - 21049

                     if(!sicc_validaciones_generales('datosAdicionalesObligatorios') || !sicc_validaciones_generales('datosAdicionales')) {
                      return;
           }

                     if(get('formulario.cbBaseCalculo') == get('formulario.oidBC_Monto')) {
                                if(!sicc_validaciones_generales('datosAdicionalesObligatoriosTipoVenta')) {
                                return;
                                }    
                     }

           if(document.getElementById("capa1").style.visibility=='visible') {
                      //2° Luego se validan los campos Numeros periodos evaluar, minimo pedidos y minimo pedidos 
                      //recomendada
                      if(!sicc_validaciones_generales('recomendadas')) {
                                  return;
                      }
           }

           tomaValoresPantalla();
           onClickPestanyaHabilitada(valor, 'formulario', 'LPMantenerParametrosConsultoras');
}

    function deshabilitaCampos(){
                      document.getElementById("txtMontoMinimo").disabled = true;
                      document.getElementById("txtUnidadesMinimas").disabled = true;
                      document.getElementById("ckReingresos").disabled = true;
                      document.getElementById("cbBaseCalculo").disabled = true;
                      document.getElementById("cbTipoVenta").disabled = true;
                      document.getElementById("RecomendacionEfectiva").disabled = true;
                      document.getElementById("cbEfectividad").disabled = true;
                      document.getElementById("cbPeriodoInicialEvaluacion").disabled = true;
                      document.getElementById("txtNumPeriodosEvaluar").disabled = true;
                      document.getElementById("txtNumMinPedidos").disabled = true;
                      document.getElementById("txtNumMinPedidosRecomendadas").disabled = true;
                      document.getElementById("indPremioCampEfect").disabled = true;
                                             // vbongiov -- Cambio 20080580 -- 13/8/2008
                                             document.getElementById("indGenerarPuntajeARecomendadas").disabled = true;
                                             document.getElementById("cbConcursoPuntajeRecomendada").disabled = true;
    }

    function marcaSeleccionDelUsuario(pControl, datos){
                      datos = datos.split(",");
                      var comb = pControl;
                      set(comb, datos);
           }

    function fGuardar() {
           accionGuardar();
    }

    function onTabParticipante(){
           if( document.getElementById("capa1").style.visibility == 'hidden')
                focaliza('formulario.txtMontoMinimo');
           else
                focaliza('formulario.RecomendacionEfectiva');
    }

    function onShTabMontoMinimo() {
           if( document.getElementById("capa1").style.visibility == 'hidden')
                focalizaBotonHTML('botonContenido','btnParticipantes');
           else
                focalizaBotonHTML('botonContenido','btnMontoVentas');
    }


function onChangeGenerarPuntaj() {
      if(get('formulario.indGenerarPuntajeARecomendadas')=="S") {
            accion('formulario.cbConcursoPuntajeRecomendada', '.disabled=false');  
      } else {
            set('formulario.cbConcursoPuntajeRecomendada', '');            
            accion('formulario.cbConcursoPuntajeRecomendada', '.disabled=true'); 
      }
  }
