PAGINA.JAVASCRIPT:


function onLoadPag(){
         configurarMenuSecundario("formulario");
         btnProxy(1,'1');
         btnProxy(5,'1');
         asignarLabels();
         asignarCombos();
         asignarChecks();
         asignarObservaciones();

         var menu = get("formulario.opcionMenu");
         if (menu.toLowerCase() =="consultar concurso"){
                  btnProxy(1,'0');
                  btnProxy(5,'0');
                  deshabilitaCampos();
                  //El area de texto no se deshabilita, se pone en read only
                  //para que el usuario pueda usar la barra de scroll vertical
                  document.getElementById("atxtObservaciones").readOnly = true;
         }
         else if(menu.toLowerCase() =="modificar concurso") {
                  btnProxy(2,'1');
                  btnProxy(3,'0');
                  btnProxy(7,'0');
                  btnProxy(8,'0');
                  btnProxy(9,'0');
         }
         else {
                  focoNombreConcurso();
         }
	 //jrivas 30-1-2007 Dupla Cyzone
	 if(get('formulario.hIndEstadoCalificion')!='S'){
    	        set('formulario.ckDuplaCyzone', 'N');
		accion("formulario.ckDuplaCyzone", ".disabled=true");
	 }

         finCargaPagina = true;
}

function fVolver() {
         window.close();
}

function asignarObservaciones() {
         var obser = get("formulario.tempObservaciones");
         set("formulario.atxtObservaciones", obser);
}

function fLimpiar() {
         onLoadPag();
}

