  accion("formulario.ckFaltantesNoAnunciados", ".disabled=true");
  accion("formulario.ckConcursoPruebas", ".disabled=true");
  accion("formulario.cbTipoConcursoIVR", ".disabled=true");

  //jrivas 1-2-2007 Dupla Cyzone
  accion("formulario.ckDuplaCyzone", ".disabled=true");

  //agregado por cvalenzu - cambio fac-003
  accion("formulario.cbTipoPrograma", ".disabled=true");

  // vbongiov -- Cambio 20080809 -- 7/04/2009
  accion("formulario.ckNoGeneraPunt", ".disabled=true");

  // vbongiov -- Cambio 20090930 -- 16/07/2009
