         focaliza("formulario.atxtObservaciones");
}

function asignarLabels(){
  txt_to('lbldtNumPlantilla', get('formulario.hdtNumPlantilla'));
  txt_to('lbldtNombrePlantilla', get('formulario.hdtNombrePlantilla'));
  txt_to('lblCanaldt', get('formulario.hDescCanal'));
  txt_to('lbldtDirigida',  get('formulario.hDirigidoA') );
}

function asignarCombos(){
  var marca = get('formulario.hMarca');
  var accesos = get('formulario.hAccesos');  
  var periodoDesde = get('formulario.hOidPeriodoDesde');    
  var periodoHasta = get('formulario.hOidPeriodoHasta');  
  var tipoConcursoIVR = get('formulario.hTipoConcursoIVR');

  // vbongiov -- Cambio 20090930 -- 16/07/2009
  var clasificacionConcurso = get('formulario.hClasificacionConcurso');

  if(marca != null && marca != ''){
    set('formulario.cbMarca', [marca]);
    //recargaComboDesde();
    //recargaComboHasta(); 
	onChangeMarca();	
	//onChangePeriodoDesde();	
  }

  if(tipoConcursoIVR != null && tipoConcursoIVR != ''){
    set('formulario.cbTipoConcursoIVR', [tipoConcursoIVR]);
  }

  if(accesos != null && accesos != ''){
    var vAccesos = accesos.split('|');
    set('formulario.cbAcceso', vAccesos);
  }

  var tipoPrograma = get('formulario.hTipoPrograma');
  if(tipoPrograma != null && tipoPrograma != ''){
	set('formulario.cbTipoPrograma', [tipoPrograma]);
  }

  // vbongiov -- Cambio 20090930 -- 16/07/2009
  if(clasificacionConcurso != null && clasificacionConcurso != ''){
	set('formulario.cbClasificacionConcurso', [clasificacionConcurso]);
  }

  // sapaza -- PER-SiCC-2011-0042 -- 18/01/2011
  var tipoOfertaConcurso = get('formulario.hTipoOfertaConcurso');
  if(tipoOfertaConcurso != null && tipoOfertaConcurso != ''){
	set('formulario.cbTipoOfertaConcurso', [tipoOfertaConcurso]);
  }

}

function recargaComboDesde() {
     recargaCombo("formulario.cbPeriodoDesde","CRAObtienePeriodos", "es.indra.sicc.dtos.cra.DTOPeriodo",armarArrayDTOPeriodo(),"marcaCombo(datos);");
}

function recargaComboHasta() {
      recargaCombo("formulario.cbPeriodoDeHasta","CRAObtienePeriodos", "es.indra.sicc.dtos.cra.DTOPeriodo",armarArrayDTOPeriodo(),"marcaComboHasta(datos);");
}

function marcaCombo(datos) {
         // Setea espacio en blanco.
         var datosNuevo = new Array();

         datosNuevo[0] = ["",""];

         for (var i = 0; i < datos.length; i++ )
                  datosNuevo[i+1]  = datos[i];

         set_combo("formulario.cbPeriodoDesde", datosNuevo);

         var oidDesde = get("formulario.hOidPeriodoDesde");

         var oidSeleccionada = new Array();
         oidSeleccionada = oidDesde.split(",");	  

  set("formulario.cbPeriodoDesde", oidSeleccionada);

}

function marcaComboHasta(datos) {
         // Setea espacio en blanco.
         var datosNuevo = new Array();

         datosNuevo[0] = ["",""];

         for (var i = 0; i < datos.length; i++ )
                  datosNuevo[i+1]  = datos[i];

         set_combo("formulario.cbPeriodoHasta", datosNuevo);

         var oidHasta = get("formulario.hOidPeriodoHasta");

         var oidSeleccionada = new Array();
         oidSeleccionada = oidHasta.split(",");	  

  set("formulario.cbPeriodoHasta", oidSeleccionada);
}

function asignarChecks(){
  var activarConcurso = get('formulario.hIndActivarConcurso');
  var devoluciones = get('formulario.hIndDevoluciones');
  var anulaciones = get('formulario.hIndAnulaciones');
  var faltantesNoAnunciados = get('formulario.hIndFaltantesNoAnunciados');
  var pruebas = get('formulario.hIndPruebas');
  //jrivas 30-1-2007 Dupla Cyzone
  var dupla = get('formulario.hIndDuplaCyzones');

  // vbongiov -- Cambio 20080809 -- 7/04/2009
  var noGeneraPunt = get('formulario.hIndNoGeneraPunt');

  // sapaza -- PER-SiCC-2012-0082 -- 16/05/2012
  var multiMarca = get('formulario.hIndMultiMarca');

  if(activarConcurso == 'S'){
    set('formulario.ckActivarConcurso', 'S');
  }else{
    set('formulario.ckActivarConcurso', 'N');
  }

  if(devoluciones == 'S'){
    set('formulario.ckDevoluciones', 'S');
  }else{
    set('formulario.ckDevoluciones', 'N');
  }

  if(anulaciones == 'S'){
    set('formulario.ckAnulaciones', 'S');
  }else{
    set('formulario.ckAnulaciones', 'N');
  }

  if(faltantesNoAnunciados == 'S'){
    set('formulario.ckFaltantesNoAnunciados', 'S');
  }else{
    set('formulario.ckFaltantesNoAnunciados', 'N');
  }

  if(pruebas == 'S'){
    set('formulario.ckConcursoPruebas', 'S');
  }else{
    set('formulario.ckConcursoPruebas', 'N');
  }

  //jrivas 30-1-2007 Dupla Cyzone
  if(dupla == 'S'){
    set('formulario.ckDuplaCyzone', 'S');
  }else{
    set('formulario.ckDuplaCyzone', 'N');
  }

  // vbongiov -- Cambio 20080809 -- 7/04/2009
  if(noGeneraPunt == 'S'){
    set('formulario.ckNoGeneraPunt', 'S');
  }else{
    set('formulario.ckNoGeneraPunt', 'N');
  }

  // sapaza -- PER-SiCC-2012-0082 -- 16/05/2012
  if(multiMarca == 'S'){
    set('formulario.ckMultiMarca', 'S');
  }else{
    set('formulario.ckMultiMarca', 'N');
  }

  cambioMultimarca();

}

function pasarCamposAHiddens(){
  pasarChecksAHiddens();
  pasarCombosAHiddens();
}

function pasarChecksAHiddens(){
  set('formulario.hIndActivarConcurso', get('formulario.ckActivarConcurso'));
  set('formulario.hIndDevoluciones', get('formulario.ckDevoluciones'));
  set('formulario.hIndAnulaciones', get('formulario.ckAnulaciones'));
  set('formulario.hIndFaltantesNoAnunciados', get('formulario.ckFaltantesNoAnunciados'));
  set('formulario.hIndPruebas', get('formulario.ckConcursoPruebas'));
  //jrivas 30-1-2007 Dupla Cyzone
  set('formulario.hIndDuplaCyzones', get('formulario.ckDuplaCyzone'));

  // vbongiov -- Cambio 20080809 -- 7/04/2009
  set('formulario.hIndNoGeneraPunt', get('formulario.ckNoGeneraPunt'));

  // vbongiov -- Cambio 20080809 -- 7/04/2009
  set('formulario.hIndMultiMarca', get('formulario.ckMultiMarca'));
}

function pasarCombosAHiddens(){
  set('formulario.hMarca', get('formulario.cbMarca')[0]);
  set('formulario.hAccesos', obtenerAccesosSeleccionados());  
  set('formulario.hOidPeriodoDesde', get('formulario.cbPeriodoDesde')[0]);
  set('formulario.hOidPeriodoHasta', get('formulario.cbPeriodoHasta')[0]);
  //Agregado por cvalenzu - 31/05/2006
  set('formulario.codPeriodoDesde', get('formulario.cbPeriodoDesde', "T"));
  set('formulario.codPeriodoHasta', get('formulario.cbPeriodoHasta', "T"));
  //FIN - Agregado por cvalenzu - 31/05/2006
  set('formulario.hTipoConcursoIVR', get('formulario.cbTipoConcursoIVR')[0]);

  //Agregado por cvalenzu - 3/7/2007 - cambio fac-003
  set('formulario.hTipoPrograma', get('formulario.cbTipoPrograma')[0]);
  //Agregado por cvalenzu - 3/7/2007

  // vbongiov -- Cambio 20090930 -- 16/07/2009
  set('formulario.hClasificacionConcurso', get('formulario.cbClasificacionConcurso')[0]);

  // sapaza -- PER-SiCC-2011-0042 -- 18/01/2011
  var tipoOfertaConcurso = get('formulario.cbTipoOfertaConcurso');
  if(tipoOfertaConcurso.length > 0)
  	set('formulario.hTipoOfertaConcurso', get('formulario.cbTipoOfertaConcurso')[0]);
  else	
	set('formulario.hTipoOfertaConcurso', '');
}

function obtenerAccesosSeleccionados(){
  var sAccesos = '';
  var accesos = get('formulario.cbAcceso');

  for(var i = 0; i < accesos.length; i++){
    if(sAccesos != ''){
      sAccesos += '|';
    }
    sAccesos += accesos[i];
  }

  return sAccesos;
}

function onClickPestanyaPagina(numPestanya, form, lp){

  // Para validar formatos y que el combo Marca tenga valor

  if ( sicc_validaciones_generales() ) {  
           pasarCamposAHiddens();
           onClickPestanyaHabilitadaAux(numPestanya, form, lp);
         } else{
                  return; //Cleal - 21049
         }

}

