function onChangePeriodoDesde() {
	//alert("onChangePeriodoDesde");
    var periodoDesde = get('formulario.cbPeriodoDesde');	
	//alert("periodoDesde " + periodoDesde);

	if(periodoDesde!="") {
		recargaCombo('formulario.cbPeriodoHasta', 'CRAObtenerPeriodosPosterioresActivos', 'es.indra.sicc.dtos.cra.DTOPeriodo', armarArrayDTOPeriodoComboHasta(), "despuesCargarComboHasta(datos);");
	}
	else {
		var array = new Array();
        array[0] = ['', ''];    
        set_combo('formulario.cbPeriodoHasta', array);         
	}	
}

function despuesCargarComboHasta(datos) {
	//alert("despuesCargarComboHasta");

	var codPeriodoHasta = get("formulario.codPeriodoHasta");
	var oidPeriodoHasta = get("formulario.oidPeriodoHasta");

	//alert("codPeriodoHasta " + codPeriodoHasta);
	//alert("oidPeriodoHasta " + oidPeriodoHasta);

	var datosNuevo = new Array(); 
    datosNuevo[0] = ["",""];

    for (var i = 0; i < datos.length; i++ )
		datosNuevo[i+1]  = datos[i];

    set_combo("formulario.cbPeriodoHasta", datosNuevo);

	var menu = get("formulario.opcionMenu");
	//1° Si la opcion del menu es modificar
	if (menu.toLowerCase() == "modificar concurso" ||
		menu.toLowerCase() == "crear concurso") {		
		var comboHasta = document.getElementById("cbPeriodoHasta");
		var longitud = comboHasta.options.length;
		//alert("longitud " + longitud);
		var existe = "false";

		for(var i=0; i<longitud; i++) {				
			var oidTemp = comboHasta.options[i].value; 
			if(oidPeriodoHasta==oidTemp) existe = "true";
		}		

		//alert("existe " + existe);

		if(existe=="true") {
			var oidSeleccionado = new Array();
			oidSeleccionado = oidPeriodoHasta.split(",");	    
			set("formulario.cbPeriodoHasta", oidSeleccionado);
		}
		else {
			//"El periodo hasta % no es posterior al período desde"
			GestionarMensaje('INC054', codPeriodoHasta);
		}
	}
}

function armarArrayDTOPeriodoComboHasta(){
         var pais = get('formulario.hPais');
         var marca = get('formulario.cbMarca');
         var canal = get('formulario.hCanal'); 
		 var periodoDesde = get('formulario.cbPeriodoDesde');

         var array = new Array();
         var index = 0;

         if( pais != null && pais != '' ){
	         array[index] = new Array('pais', pais);
             index++;
         }	

         if( marca != null && marca[0] != null && marca[0] != '' ){
		     array[index] = new Array('marca', marca[0]);
             index++;
         }

         if( canal != null && canal != '' ){
			 array[index] = new Array('canal', canal);
             index++;
         }

		 if( periodoDesde != null && periodoDesde != '' ){
			 array[index] = new Array('oid', periodoDesde);
             index++;
         }

         return array;
}

// Funcion modificada por Cristian Valenzuela
// 3-5-2006 - Cambios de versionado
function onChangeMarca() {
	//alert("onChangeMarca");

	var codPeriodoDesde = get("formulario.codPeriodoDesde");
	var oidPeriodoDesde = get("formulario.oidPeriodoDesde");
	var codPeriodoHasta = get("formulario.codPeriodoHasta");
	var oidPeriodoHasta = get("formulario.oidPeriodoHasta");

	//alert("codPeriodoDesde " + codPeriodoDesde);
	//alert("oidPeriodoDesde " + oidPeriodoDesde);
	//alert("codPeriodoHasta " + codPeriodoHasta);
	//alert("oidPeriodoHasta " + oidPeriodoHasta);

	var menu = get("formulario.opcionMenu");
	var oidVigenciaConcurso = get("formulario.oidVigenciaConcurso");

	//1° Si la opcion del menu es distinta a consulta concurso
    if (menu.toLowerCase() != "consultar concurso"){
    	if((menu.toLowerCase() == "modificar concurso") && oidVigenciaConcurso == '1') {
	    	var array = new Array();
		    array[0] = [oidPeriodoDesde, codPeriodoDesde];    
	        set_combo('formulario.cbPeriodoDesde', array);

            var oidMarca = get('formulario.cbMarca');
            var desMarca = get('formulario.cbMarca', "T");
	        var array2 = new Array();
		    array2[0] = [oidMarca, desMarca];    
	        set_combo('formulario.cbMarca', array2);

	        onChangePeriodoDesde();	 
    	} else {
			//alert("limpia combo periodo hasta");	
			//2° Si limpia el combo periodo hasta
			var array = new Array();
	        array[0] = ['', ''];    
	        set_combo('formulario.cbPeriodoHasta', array);         

			//alert("carga combo periodo desde");	
			//3° Se carga el combo periodo desde
			recargaCombo('formulario.cbPeriodoDesde', 'CRAObtenerPeriodosPosterioresActual', 'es.indra.sicc.dtos.cra.DTOPeriodo', armarArrayDTOPeriodo(), "despuesCargarComboDesde(datos);");
		}			
	}
	else {
		//alert("entro al else");
		var array = new Array();
	    array[0] = [oidPeriodoDesde, codPeriodoDesde];    
        set_combo('formulario.cbPeriodoDesde', array);         

		var array = new Array();
        array[0] = [oidPeriodoHasta, codPeriodoHasta];    
        set_combo('formulario.cbPeriodoHasta', array);         		
	}
}

function despuesCargarComboDesde(datos) {
	//alert("despuesCargarComboDesde");

	var codPeriodoDesde = get("formulario.codPeriodoDesde");
	var oidPeriodoDesde = get("formulario.oidPeriodoDesde");
	var codPeriodoHasta = get("formulario.codPeriodoHasta");
	var oidPeriodoHasta = get("formulario.oidPeriodoHasta");

	//alert("codPeriodoDesde " + codPeriodoDesde);
	//alert("oidPeriodoDesde " + oidPeriodoDesde);
	//alert("codPeriodoHasta " + codPeriodoHasta);
	//alert("oidPeriodoHasta " + oidPeriodoHasta);

	var datosNuevo = new Array(); 
    datosNuevo[0] = ["",""];

    for (var i = 0; i < datos.length; i++ )
		datosNuevo[i+1]  = datos[i];

	//alert("cargando combo desde");
    set_combo("formulario.cbPeriodoDesde", datosNuevo);

	var menu = get("formulario.opcionMenu");
	if (menu.toLowerCase() == "modificar concurso" ||
		menu.toLowerCase() == "crear concurso"){
		//alert("entro al if");	

		var comboDesde = document.getElementById("cbPeriodoDesde");
		var longitud = comboDesde.options.length;
		//alert("longitud " + longitud);
		var existe = "false";

		for(var i=0; i<longitud; i++) {				
			var oidTemp = comboDesde.options[i].value; 
			//alert("oidTemp " + oidTemp);
			//alert("oidPeriodoDesde " + oidPeriodoDesde);
			if(oidPeriodoDesde==oidTemp) {
				existe = "true";
			}
		}		

		//alert("existe " + existe)
		if(existe=="true") {
			//alert("selecciona");
			var oidSeleccionado = new Array();
			oidSeleccionado = oidPeriodoDesde.split(",");	    
			set("formulario.cbPeriodoDesde", oidSeleccionado);

			onChangePeriodoDesde();	
		}
		else {
			//alert("mensaje");
			//"El periodo desde % no es posterior al período actual"
			GestionarMensaje('INC053', codPeriodoDesde);
		}
	}
}

function armarArrayDTOPeriodo(){
         var pais = get('formulario.hPais');
         var marca = get('formulario.cbMarca');
         var canal = get('formulario.hCanal');   

         var array = new Array();
         var index = 0;

         if( pais != null && pais != '' ){
                  array[index] = new Array('pais', pais);
                  index++;
         }	

         if( marca != null && marca[0] != null && marca[0] != '' ){
                  array[index] = new Array('marca', marca[0]);
                  index++;
         }

         if( canal != null && canal != '' ){
                  array[index] = new Array('canal', canal);
                  index++;
         }

         return array;
}

function comprobarDatosObligatorios(){
         // Comprueba que los campos marcados como obligatorios, tengan valor .
         // Si alguno no contiene valor, retorna false. Si todos están rellenos, retorna true.
         // Nota especial con el combo Marca, que no se valida acá porque debe poseer un valor sí o sí para pasar de pantalla.

         var txtNombreConcurso = get('formulario.txtNombreConcurso');
         var cbPeriodoDesde = get('formulario.cbPeriodoDesde');
         var txtExpresionPuntaje = get('formulario.txtExpresionPuntaje');
         var retorno = false;

         if (txtNombreConcurso != "" && cbPeriodoDesde != "" && txtExpresionPuntaje != "")
                  retorno = true;

         return retorno;
}

function fGuardar() {
		if(!sicc_validaciones_generales('camposOpcionales')) {
			return;
        }	

         // Modificado por ssantana, 30/5/2005
         if ( sicc_validaciones_generales() ) {
                  var validacionObligatorios = comprobarDatosObligatorios();
                  //alert(validacionObligatorios);

                  if ( validacionObligatorios ){
                           set('formulario.datosObligatorios', 'S');
                           set("formulario.hOidPeriodoDesde" , get("formulario.cbPeriodoDesde"));
                           set("formulario.hOidPeriodoHasta", get("formulario.cbPeriodoHasta"));
                           set("formulario.hMarca", get("formulario.cbMarca"));
                           set("formulario.hCanal",get("formulario.hCanal"));
                  }else
                           set('formulario.datosObligatorios', 'N');

                  pasarCamposAHiddens();

                  eval('formulario').oculto = 'S';
                  set('formulario.conectorAction', 'LPMantenerParametrosGenerales');
                  set('formulario.accion', 'guardar');
                  enviaSICC("formulario", "", "", "N");
         } else{
                  return; //Cleal - 21049
         }
}

function onchangeAcceso() {
         var menu = get("formulario.opcionMenu");
         if (menu.toLowerCase() =="consultar concurso"){
                  var accesos = get('formulario.hAccesos');

                  if(accesos != null && accesos != ''){
                           var vAccesos = accesos.split('|');
                      set('formulario.cbAcceso', vAccesos);
                    }
             return false;
         }
}

function cambioMultimarca() {
	if(get('formulario.ckMultiMarca')=="S") {
		accion("formulario.txtPuntosAbonar", ".disabled=false");
	}
	else {
		accion("formulario.txtPuntosAbonar", ".disabled=true");
		set("formulario.txtPuntosAbonar", "");
	}
}
