  varNoLimpiarSICC='S'; 

  function onLoadPag() {
      configurarMenuSecundario('formulario');

	  // sapaza -- PER-SiCC-2010-0210 -- 23/04/2010
      document.getElementById("lblPeriodoInicio").style.display='none';
      document.getElementById("cbPeriodoInicio").style.display='none';
	  document.getElementById("lblPeriodoFinal").style.display='none';

      if(!fMostrarMensajeError()) {

            cargarPantallaPlantilla();

            // Ya se han  cargado datos para esta pantalla
            if(get('formulario.hCbTipoSeleccion')!="") {
                  cargarPantalla();           
            }

            if(get('formulario.opcionMenu')=="Consultar Concurso"){  
                  deshabilitarPantalla();
                  btnProxy(1,0);
                  btnProxy(2,1);
                  btnProxy(3,0);
                  btnProxy(4,0);
                  btnProxy(5,0);
                  btnProxy(7,0);
                  btnProxy(8,0);
                  btnProxy(9,0);

            }
			else if(get('formulario.opcionMenu')=="Modificar Concurso" || 
                         get('formulario.opcionMenu')=="nueva version"){  
                  btnProxy(1,1);
                  btnProxy(2,1);
                  btnProxy(3,0);
                  btnProxy(4,0);
                  btnProxy(5,1);
                  btnProxy(7,0);
                  btnProxy(8,0);
                  btnProxy(9,0);

                  // Oculto las pestanias
                  if(get('formulario.opcionMenu')=="nueva version"){ 
                      document.getElementById('tblPestanyas1').style.display='none';
                      document.getElementById('tblPestanyas2').style.display='none';
                  }

                  focaliza("formulario.cbTipoSeleccion");

            } else {
                  focaliza("formulario.cbTipoSeleccion");
            }

      } 

      finCargaPagina = true;

}

// vbongiov -- Cambio 20080811 -- 26/08/2009
function accionBuscarProducto(){  
	  // Busca el producto
	  var obj = new Object();
	  obj.casoDeUso= 'buscarProducto' 
	  obj.accion = '';
	  obj.oidMarcaCorporativa = get('formulario.hOidMarca');
	  var retorno = mostrarModalSICC('LPBuscarProductosDTO', '', obj, null, null);

	  if(retorno!=null) {          
		  // Productos seleccionados
		  productosString = retorno[0];      
		  productosString = productosString.substring(1,productosString.length);

		  productos = productosString.split('$');
		  //alert("productos: " + productos);

		  // Verifica que se haya seleccionado un producto
		  if(productos.length==1 ){
				producto = productos[0].split('|');
				set('formulario.txtCodProducto', producto[1]); 
				set('formulario.oidProducto', producto[0]); 
		  } else {
				set('formulario.txtCodProducto', ''); 
				set('formulario.oidProducto', ''); 
				GestionarMensaje('1497');
		  }
	  }
  }

