}

  function cargarPantalla() {
      // Seleccion de los combos
      iSeleccionado = new Array(); 

      iSeleccionado[0] = get('formulario.hCbTipoSeleccion');
      set('formulario.cbTipoSeleccion', iSeleccionado);

      iSeleccionado[0] = get('formulario.hCbPeriodo');
	  //DBLG700000042 - gPineda - 02/10/06
	  if( get( 'formulario.ckPeriodoDespacho') == "S" ){
		accion('formulario.cbPeriodo', '.disabled=false');
		set('formulario.cbPeriodo', iSeleccionado);
		accion('formulario.txtNumPeriodoObtencion', '.disabled=false');

		// sapaza -- PER-SiCC-2010-0210 -- 23/04/2010
		if(get('formulario.cbTipoSeleccion')== get('formulario.OID_TIPO_PREM_BOLSA')) {
		  iSeleccionado[0] = get('formulario.hCbPeriodoInicio');
		  accion('formulario.cbPeriodoInicio', '.disabled=false');
		  set('formulario.cbPeriodoInicio', iSeleccionado);
		}  

//		set('formulario.ckPeriodoDespacho', 'S');
	  }

      iSeleccionado[0] = get('formulario.hCbTipoEleccion');
      set('formulario.cbTipoEleccion', iSeleccionado);

      // Visualización de la capa de niveles
      if(get('formulario.cbTipoSeleccion')== get('formulario.OID_TIPO_PREM_NIVELES'))            
            visibilidad('capa3','V');

	  // sapaza -- PER-SiCC-2010-0210 -- 23/04/2010
      if(get('formulario.cbTipoSeleccion')== get('formulario.OID_TIPO_PREM_BOLSA')) {
        document.getElementById("lblPeriodoInicio").style.display='inline';
	    document.getElementById("cbPeriodoInicio").style.display='inline';
	    document.getElementById("lblPeriodoFinal").style.display='inline';
	    document.getElementById("lblPeriodo").style.display='none';
	  } 
	  else {
        document.getElementById("lblPeriodoInicio").style.display='none';
	    document.getElementById("cbPeriodoInicio").style.display='none';
	    document.getElementById("lblPeriodoFinal").style.display='none';
	    document.getElementById("lblPeriodo").style.display='inline';
	  }  


	  onChangePremiosElectivos();

      // vbongiov -- Cambio 20080811 -- 26/08/2009
	  if(get('formulario.cbTipoSeleccion')== get('formulario.OID_TIPO_PREMIACION_SORTEO')) {   
            visibilidad('capa2','O');
			visibilidad('capa3','O');
			visibilidad('capa4','V');
	  }
  }

  function cargarPantallaPlantilla() {
      // Deshabilita flag según la plantilla
      if(get('formulario.hIndPremiosAcumuPorNiveles')==get('formulario.IND_INACTIVO'))
            accion('formulario.ckPremiosAcumulativosNiveles', '.disabled=true');

      if(get('formulario.hIndNivelesRotativos')==get('formulario.IND_INACTIVO'))
            accion('formulario.ckNivelesRotativos', '.disabled=true');    

      if(get('formulario.hIndPremiosElectivos')==get('formulario.IND_INACTIVO')) {
            accion('formulario.ckPremioElectivo', '.disabled=true');         
            accion('formulario.cbTipoEleccion', '.disabled=true');  

            // sapaza -- PER-SiCC-2011-0857 -- 23/01/2012
            accion('formulario.ckSinPremioPorDefecto', '.disabled=true');       
      }
	  else {
            accion('formulario.cbTipoEleccion', '.disabled=true'); 
            accion('formulario.ckSinPremioPorDefecto', '.disabled=true');         
      }

	  // sapaza -- PER-SiCC-2010-0388 -- 14/07/2010
      if(get('formulario.hIndAccesoNivelPremioSuperior')==get('formulario.IND_INACTIVO'))
            accion('formulario.ckAccesoNivelPremioSuperior', '.disabled=true');    

      // Oculta la capa de niveles    
      visibilidad('capa3','O');

	  // vbongiov -- Cambio 20080811 -- 26/08/2009
	  visibilidad('capa4','O');
  }

