      deshabilitarHabilitarBoton('botonContenido','btnAceptar','D')
  }


  function onChangePremiosElectivos() {

      if(get('formulario.ckPremioElectivo')=="S") {
           accion('formulario.cbTipoEleccion', '.disabled=false'); 
		   var iSeleccionado = new Array(); 
		   iSeleccionado[0] = get('formulario.hCbTipoEleccion');
		   set('formulario.cbTipoEleccion', iSeleccionado);

		   // sapaza -- PER-SiCC-2011-0857 -- 23/01/2012
		   accion('formulario.ckSinPremioPorDefecto', '.disabled=false');

      } else if(get('formulario.ckPremioElectivo')=="N") {

           // Validacion de premios electivos
           cantidadPremiosArticulos = get('formulario.cantidadPremiosArticulos');
           cantidadPremiosElectivos = cantidadPremiosArticulos.split("|");
           cambioOK = "S";

           for(var i=0; i<cantidadPremiosElectivos.length; i++) {
              if(Number(cantidadPremiosElectivos[i]) > 1) {
                    cambioOK = "N";                   
              }
           }

           if(cambioOK=="S") {
                set('formulario.cbTipoEleccion', '');
                accion('formulario.cbTipoEleccion', '.disabled=true'); 

                // sapaza -- PER-SiCC-2011-0857 -- 23/01/2012
                set('formulario.ckSinPremioPorDefecto', 'N');
                accion('formulario.ckSinPremioPorDefecto', '.disabled=true');
           } else {
                GestionarMensaje("1560");
                set('formulario.ckPremioElectivo', 'S');
           }
      }
  }

  function onChangePeriodoDespacho() {
      if(get('formulario.ckPeriodoDespacho')=="S") {
           accion('formulario.cbPeriodo', '.disabled=false');    
           accion('formulario.txtNumPeriodoObtencion', '.disabled=false'); 

           if(get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREM_BOLSA')){
             accion('formulario.cbPeriodoInicio', '.disabled=false'); 
           }

      } else {
           set('formulario.cbPeriodo', '');
           set('formulario.txtNumPeriodoObtencion', '');
           accion('formulario.cbPeriodo', '.disabled=true');    
           accion('formulario.txtNumPeriodoObtencion', '.disabled=true');

           if(get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREM_BOLSA')){
              set('formulario.cbPeriodoInicio', '');
              accion('formulario.cbPeriodoInicio', '.disabled=true');
           }

      }
  }

