  function accionAceptar() {      
      onChangeTipo = true;
      borraNiveles = false;

      // Verifica la seleccion previa
      if(get('formulario.hCbTipoSeleccion')!="" && get('formulario.hCbTipoSeleccion')!=get('formulario.cbTipoSeleccion')) {
            onChangeTipo = GestionarMensaje('1580');

            if(onChangeTipo){
                  borraNiveles = true;
            }
      }

      if(onChangeTipo){
            set('formulario.hCbTipoSeleccion', get('formulario.cbTipoSeleccion'));   

			if(get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREMIACION_SORTEO')){      

				  visibilidad('capa2','O');
				  visibilidad('capa3','O');
				  visibilidad('capa4','V');

            } else if(get('formulario.hCbTipoSeleccion')!= get('formulario.OID_TIPO_PREM_NIVELES')){                      
                  set('formulario.ckPremiosAcumulativosNiveles', 'N');    
				  set('formulario.txtHastaNivel', '');    
                  set('formulario.ckNivelesRotativos', 'N');    
                  set('formulario.txtNumRotaciones', '');    
                  set('formulario.ckAccesoNivelPremioSuperior', 'N');    
                  visibilidad('capa3','O');

                  // sapaza -- PER-SiCC-2010-0210 -- 23/04/2010
                  if(get('formulario.ckPeriodoDespacho')=="S")
                    accion('formulario.cbPeriodoInicio', '.disabled=false');
                  else
                    accion('formulario.cbPeriodoInicio', '.disabled=true');

                  document.getElementById("lblPeriodoInicio").style.display='inline';
				  document.getElementById("cbPeriodoInicio").style.display='inline';
				  document.getElementById("lblPeriodoFinal").style.display='inline';
	    		  document.getElementById("lblPeriodo").style.display='none';

            }
			else {
                  // Valores default
                  cargarPantallaPlantilla2();

				  // sapaza -- PER-SiCC-2010-0388 -- 14/07/2010
				  set('formulario.ckAccesoNivelPremioSuperior', "N");
                  /*if(get('formulario.hIndFaseCalificacion')=="S" && get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREM_NIVELES')) {
                      set('formulario.ckAccesoNivelPremioSuperior', "S");
                  }
				  else {
                      set('formulario.ckAccesoNivelPremioSuperior', "N");
                  }*/

                  set('formulario.txtNumRotaciones', '1');                                 
                  visibilidad('capa3','V');
				  onChangeChkPremios();

				  // sapaza -- PER-SiCC-2010-0210 -- 23/04/2010
                  document.getElementById("lblPeriodoInicio").style.display='none';
				  document.getElementById("cbPeriodoInicio").style.display='none';
				  document.getElementById("lblPeriodoFinal").style.display='none';
	    		  document.getElementById("lblPeriodo").style.display='inline';

            }

            if(borraNiveles){
                  // Modifica el dtoNivelesPremiacion en session
                  set('formulario.conectorAction', 'LPMantenerParametrosGeneralesPremiacion');
                  set('formulario.accion', 'eliminar niveles');
                  eval('formulario').oculto = 'S';
                  enviaSICC('formulario', null, null, 'N');
            }            
      }
	  else {
            // No se desea modificar el tipo premiacion
            iSeleccionado = new Array(); 
            iSeleccionado[0] = get('formulario.hCbTipoSeleccion');            
            set('formulario.cbTipoSeleccion', iSeleccionado);
      }
  }

  //Funcion modificada por Cristian Valenzuela - 17/2/2006
  //Incidencia 22630 - (INC-29 BLOQUEANTE)
  function comprobarDatosObligatorios (){
    var datosObligatorios = true;

    if(get('formulario.hCbTipoSeleccion')== '')
          datosObligatorios = false;

    // Premiacion por niveles    
    if(get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREM_NIVELES')){ 

          if(get('formulario.txtNumRotaciones')=='')
              datosObligatorios = false;

          // Premio electivo
          if(get('formulario.ckPremioElectivo')=="S" ){
				if(get('formulario.cbTipoEleccion')=='') {
					datosObligatorios = false;          
				}		  
		  }	

    // vbongiov -- Cambio 20080811 -- 26/08/2009
    } else if(get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREMIACION_SORTEO')){ 

		if(get('formulario.txtCantidadNumerosAOtorgar')=='' && get('formulario.txtCodProducto')==''){
			datosObligatorios = false;  
		}		
	}

    return datosObligatorios;              
  }

function onClickPestanyaPremiacion(valor) {
	//Cleal - 21049
    // Premiacion por niveles    
    if(get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREM_NIVELES')){ 
		if(!sicc_validaciones_generales("Obligatorios")){
			return;
        }

        // Premio electivo
        if(get('formulario.ckPremioElectivo')=="S"){
			if(!sicc_validaciones_generales("Obligatorios2")){
				return;
            }
        }

    // vbongiov -- Cambio 20080811 -- 26/08/2009
    } else if(get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREMIACION_SORTEO')){ 

		if(!sicc_validaciones_generales("gSorteo")){
				return;
        }
	}

	if(!sicc_validaciones_generales("cambioPestania")){
		return;
    }

    onClickPestanyaHabilitadaAux(valor, 'formulario', 'LPMantenerParametrosGeneralesPremiacion');
}  

function fGuardar(){
	//Cleal - 21049
    // Premiacion por niveles    
    if(get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREM_NIVELES')){ //cbTipoSeleccion
		if(!sicc_validaciones_generales("Obligatorios")){
			return;
        }

        // Premio electivo
        if(get('formulario.ckPremioElectivo')=="S"){
			if(!sicc_validaciones_generales("Obligatorios2")){
				return;
            }
        }

    // vbongiov -- Cambio 20080811 -- 26/08/2009
    } else if(get('formulario.hCbTipoSeleccion')== get('formulario.OID_TIPO_PREMIACION_SORTEO')){ 

		if(!sicc_validaciones_generales("gSorteo")){
				return;
        }
	}

    if(sicc_validaciones_generales("gAlmacenar")){
		if(get('formulario.opcionMenu')!= 'nueva version'){
			eval('formulario').oculto = 'S';
            set('formulario.conectorAction', 'LPMantenerParametrosGeneralesPremiacion');
            set('formulario.accion', 'guardar');
            set('formulario.datosObligatorios', comprobarDatosObligatorios());
            enviaSICC('formulario','','','N');
         }
		 else {
			eval('formulario').oculto = 'S';
            set('formulario.conectorAction', 'LPMantenerParametrosGeneralesPremiacion');
            set('formulario.accion', 'almacenar');
            set('formulario.datosObligatorios', comprobarDatosObligatorios());
            enviaSICC('formulario','','','N');
         }
    }
}  

  function accionDefinirNiveles(){   

    if(sicc_validaciones_generales("gDefinir")){
          //alert("opcion Menu: " + get('formulario.opcionMenu'));
                      // dmaneiro - 20/12/2005 - inc. 21040
          set('formulario.hCbTipoSeleccion', get('formulario.cbTipoSeleccion'));   
                      var obj = new Object();
          obj.opcionMenu = get('formulario.opcionMenu')
          obj.accion = 'cargar mantener nivel';
          obj.numeroNiveles = get('formulario.txtNumNiveles');
          obj.hIndPremiosElectivosPremios = get('formulario.ckPremioElectivo');     
          obj.hCbTipoSeleccion = get('formulario.hCbTipoSeleccion');

          var retorno = mostrarModalSICC('LPMantenerParametrosGeneralesPremiacion', 'cargar mantener nivel', obj, '795', '595');

          // Validacion de premios electivos
          if(retorno!=null){
              set('formulario.cantidadPremiosArticulos',retorno[0]);
          }          

          focaliza("formulario.cbTipoSeleccion");                     
    }
          }

  function fLimpiar(){
      if(get('formulario.opcionMenu')=="Crear concurso"){

            if(GestionarMensaje('1580')){
                  set('formulario.cbTipoSeleccion', '');    
                  set('formulario.hCbTipoSeleccion', '');    
                  set('formulario.txtNumNiveles', '');    
                  set('formulario.ckPeriodoDespacho', 'N');    
                  set('formulario.cbPeriodo', '');    

                  // sapaza -- PER-SiCC-2010-0210 -- 23/04/2010
                  set('formulario.cbPeriodoInicio', '');

                  set('formulario.txtNumPeriodoObtencion', '');    
                  set('formulario.ckPremiosAcumulativosNiveles', 'N');    
                  set('formulario.ckNivelesRotativos', 'N');    
                  set('formulario.txtNumRotaciones', '1');    
                  set('formulario.ckAccesoNivelPremioSuperior', 'N');    
                  set('formulario.ckPremioElectivo', 'N');    
                  set('formulario.cbTipoEleccion', '');   

                  // sapaza -- PER-SiCC-2011-0857 -- 23/01/2012
                  set('formulario.ckSinPremioPorDefecto', 'N');

				  // vbongiov -- Cambio 20080811 -- 26/08/2009
				  set('formulario.txtCantidadNumerosAOtorgar', ''); 
				  set('formulario.txtCodProducto', ''); 
				  set('formulario.oidProducto', ''); 

                  cargarPantallaPlantilla();

                  // Modifica el dtoNivelesPremiacion en session
                  set('formulario.conectorAction', 'LPMantenerParametrosGeneralesPremiacion');
                  set('formulario.accion', 'eliminar niveles');
                  eval('formulario').oculto = 'S';
                  enviaSICC('formulario', null, null, 'N');
            }

      } else {
            set('formulario.conectorAction', 'LPMantenerParametrosGeneralesPremiacion');
            set('formulario.accion', '');
            enviaSICC('formulario','','','N');
      }
  }

