  }

  function terminaEliminarNiveles(){
    focaliza('formulario.cbTipoSeleccion');
    eval('formulario').oculto = 'N';
  }

  function focalizaTab(campo){
     if(campo==1) {
            if(get_visibilidad('capa3')){
                  if(get('formulario.hIndPremiosAcumuPorNiveles')==get('formulario.IND_INACTIVO')) {
                      if(get('formulario.hIndNivelesRotativos')==get('formulario.IND_INACTIVO')) {
                            focaliza("formulario.txtNumRotaciones");
                      } else {
                            focaliza("formulario.ckNivelesRotativos");
                      }
                  } else {
                      focaliza("formulario.ckPremiosAcumulativosNiveles");
                  }
            } else {
                  focaliza("formulario.cbTipoSeleccion");
            }
     } else if(campo==2) {
          if(get('formulario.hIndPremiosElectivos')==get('formulario.IND_INACTIVO')) {
              focaliza("formulario.cbTipoSeleccion");
          } else {
              focaliza("formulario.ckPremioElectivo");
          }
     } else if(campo==3) {
          if(get('formulario.ckPremioElectivo')=="S") {
              focaliza("formulario.cbTipoEleccion");
          } else {
              focaliza("formulario.cbTipoSeleccion");
          }
     } else if(campo==4) {
          focaliza("formulario.cbTipoSeleccion");
     }      

  }


  function focalizaShTab(){  

      if(!get_visibilidad('capa3')){
            focalizaBotonHTML('botonContenido','btnDefinirNiveles');

	  // vbongiov -- Cambio 20080811 -- 26/08/2009
	  } else if(get_visibilidad('capa4')){
			focalizaBotonHTML('botonContenido','btnBuscar');     

      } else {      
            if(get('formulario.hIndPremiosElectivos')==get('formulario.IND_INACTIVO')) {
                  focaliza("formulario.ckAccesoNivelPremioSuperior");
            }
			else {
		  		 focaliza("formulario.ckAccesoNivelPremioSuperior");
                  /*if(get('formulario.ckPremioElectivo')=="S") {
                      focaliza("formulario.cbTipoEleccion");
                  } else {
                      focaliza("formulario.ckPremioElectivo");
                  }*/
            }      
      }
  }

