	function onTabAccesoNivelPremio() {
		focaliza("formulario.cbTipoSeleccion");
	}

	function onTabTipoEleccion() {
		focalizaBotonHTML('botonContenido','btnDefinirNiveles');
	}

	function onChangeChkPremios() {
		if(get('formulario.ckPremiosAcumulativosNiveles')=="S") {
			document.getElementById("lblHastaNivel").style.display='inline';
			document.getElementById("txtHastaNivel").style.display='inline';

			if(get('formulario.txtNumNiveles')!="") {
				var numNiveles = parseInt(get('formulario.txtNumNiveles'), 10);
				numNiveles = numNiveles + 1;
				set('formulario.txtHastaNivel', numNiveles);				
			}			
		}
		else {
	 		set('formulario.txtHastaNivel', '');
			document.getElementById("lblHastaNivel").style.display='none';
			document.getElementById("txtHastaNivel").style.display='none';
		}
	}
