PAGINA.JAVASCRIPT:


var vConstantesPestInc = new Array();    

function onLoadPag() {  
	configurarMenuSecundario('formulario');
    fMostrarMensajeError();

    if (get('formulario.ocultaParticipante') == '1'){
		//Ocultar el botón btParticipante
        deshabilitarHabilitarBoton('botonContenido','btnParticipantes','D');
    }

    //Inicialmente se ocultan todas las capas menos la de datos adicionales 
    document.getElementById("capa2").style.visibility='hidden';//formaCalculo
    document.getElementById("capa3").style.visibility='hidden';//Recomendadas
    document.getElementById("capa4").style.visibility='hidden';//Monto

    marcaSeleccionDelUsuario('formulario.cbFormaCalculo', get('formulario.oidFormaCalculo'));         

    if (get('formulario.hidIndGerentes') == '1'){ 
		//Ya habia ingresado antes

        if  ( get('formulario.oidFormaCalculo') == get('formulario.oid_Forma_Calc_Base_Calc')){
	        document.getElementById("capa2").style.visibility='visible';
            marcaSeleccionDelUsuario('formulario.cbFormaCalculo', get('formulario.oidFormaCalculo'));

			if (get('formulario.oidPlantillaBaseCalculo')!= '') {                  
				marcaSeleccionDelUsuario('formulario.cbBaseCalculo', get('formulario.oidPlantillaBaseCalculo'));    
                accion("formulario.cbBaseCalculo",".disabled=true");

                if ( get('formulario.oidPlantillaBaseCalculo') == get('formulario.oidBC_Recomendada')) {
					marcaSeleccionDelUsuario('formulario.cbPeriodoInicialEvaluacion', get('formulario.hidPeriodoInicialEval'));

					document.getElementById("capa3").style.visibility='visible';
                }
                else { 
					if( get('formulario.oidPlantillaBaseCalculo') == get('formulario.oidBC_Calculo_Monto'))
						document.getElementById("capa4").style.visibility='visible';                         

					//Ocultar la capa Recomendadas con los campos:                
                    document.getElementById("capa3").style.visibility='hidden';//Recomendadas 
                    marcaSeleccionDelUsuario('formulario.cbTipoVenta', get('formulario.oidTipoVta'));

					if(get('formulario.oidTipoVta')!="" || get('formulario.oidTipoVentaPlantilla')!="") {
						accion("formulario.cbTipoVenta",".disabled=true");
					}

                    document.getElementById("capa4").style.visibility='visible';
                }
            }
            else {
				marcaSeleccionDelUsuario('formulario.cbBaseCalculo', get('formulario.oidParamBaseCalculo'));

                if ( get('formulario.oidParamBaseCalculo') == get('formulario.oidBC_Calculo_Monto')){
					marcaSeleccionDelUsuario('formulario.cbTipoVenta', get('formulario.oidTipoVta'));
					if(get('formulario.oidTipoVta')!="" || get('formulario.oidTipoVentaPlantilla')!="") {
						accion("formulario.cbTipoVenta",".disabled=true");
					}

                    document.getElementById("capa4").style.visibility='visible';
                }

                if ( get('formulario.oidParamBaseCalculo') == get('formulario.oidBC_Recomendada')){
					marcaSeleccionDelUsuario('formulario.cbPeriodoInicialEvaluacion', get('formulario.hidPeriodoInicialEval'));
                    document.getElementById("capa3").style.visibility='visible';
                }
            } 
        } 

        if(get('formulario.hIndFaseCalificacion') == '1'){
			accion("formulario.cbFormaCalculo",".disabled=true");				
        }
    } 
    else {  // Si no se ingresó antes...
		//COMIENZO - CAMBIOS INCIDENCIA 20760
        var seleccFormaCalculo = get("formulario.seleccFormaCalculo");
        var oidBaseCalculoTemp = get("formulario.oidBaseCalculoTemp");

        if(seleccFormaCalculo=="variablesVenta") {
			//Se seleccionara en el combo forma de calculo el valor
            //correspondiente a variables de venta y se deshabilita el mismo
            marcaSeleccionDelUsuario('formulario.cbFormaCalculo', get('formulario.oid_Forma_Calc_Vbles_Vta'));
            accion("formulario.cbFormaCalculo",".disabled=true");

            //Se ocultan las capas que correspondan
            document.getElementById("capa2").style.visibility='hidden';//Forma Calculo
            marcaSeleccionDelUsuario('formulario.cbBaseCalculo', "");
            document.getElementById("capa4").style.visibility='hidden';//Monto
            marcaSeleccionDelUsuario('formulario.cbTipoVenta', "");
            document.getElementById("capa3").style.visibility='hidden';//Recomendadas
            marcaSeleccionDelUsuario('formulario.cbPeriodoInicialEvaluacion', "");
        }
        else if(seleccFormaCalculo=="baseCalculo") {
			//Se seleccionara en el combo forma de calculo el valor
            //correspondiente a base de calculo y se deshabilita el mismo
            marcaSeleccionDelUsuario('formulario.cbFormaCalculo', get('formulario.oid_Forma_Calc_Base_Calc'));
            accion("formulario.cbFormaCalculo",".disabled=true");

            //Se ocultan y ponen visible las capas que correspondan
            accionAceptar();
            accionAceptarFormaCalculo();
        }

        if(oidBaseCalculoTemp!="") {
			//Se seleccionara el valor que haya en el combo base de calculo
            //y se deshabilitara el mismo
            marcaSeleccionDelUsuario('formulario.cbBaseCalculo', get('formulario.oidBaseCalculoTemp'));
            accion("formulario.cbBaseCalculo",".disabled=true");
        }
        //FIN - CAMBIOS INCIDENCIA 20760
    }

    var menu = get("formulario.opcionMenu");      
    if (menu == "Consultar Concurso"){
		deshabilitaCamposTodos();            
        btnProxy(1, 0);
        btnProxy(5, 0);
    }
    else {
		focoComboFormaCalculo();
    }

    var vConst = get('formulario.hidConstantesPestINC');
    vConstantesPestInc = vConst.split("|");      
    finCargaPagina = true;
}

function fLimpiar() {
         onLoadPag();	
}

function focoComboFormaCalculo() {
         focaliza('formulario.cbFormaCalculo');
}

function focoComboTipoVenta() {
         focaliza('formulario.cbTipoVenta');
}

function deshabilitaCampos() {
	accion('formulario.txtNumPeriodosEvaluar','.readOnly=true');
	accion('formulario.txtNumMinPedidos','.readOnly=true');
	accion('formulario.txtNumMinPedidosRecomendada','.readOnly=true');
}

function accionExitosa() {

}

function deshabilitaCamposTodos(){    
	accion("formulario.cbBaseCalculo", ".disabled=true");
    accion("formulario.cbFormaCalculo", ".disabled=true");
    accion("formulario.cbTipoVenta", ".disabled=true");
    accion("formulario.ckRecomendacionEfectiva", ".disabled=true");
    accion("formulario.cbPeriodoInicialEvaluacion", ".disabled=true");
    deshabilitaCampos();
    deshabilitarHabilitarBoton('botonContenido','btnAceptar1','D');
    deshabilitarHabilitarBoton('botonContenido','btnAceptar2','D');
}

function marcaSeleccionDelUsuario(pControl, datos){
	datos = datos.split(",");
    var comb = pControl;
    set(comb, datos);
}

function comprobarDatosObligatorios(){
	var formaCalculo = get("formulario.cbFormaCalculo");
    var baseCalculo = get("formulario.cbBaseCalculo");
    var oidFormaCalcBaseCalc = get("formulario.oid_Forma_Calc_Base_Calc");
    var oidBaseCalcMonto = get("formulario.oidBC_Calculo_Monto");
    var tipoVenta = get("formulario.cbTipoVenta");
    var retorno = true;

    // Si cbFormaCalculo está vacío, retorna false.
	if ( formaCalculo == "" ) {
		retorno = false;
    }
    else { 
		// Si en Forma de Calculo se eligió "Base Calculo", se chequea que este combo tenga valor.
        if ( parseInt(formaCalculo, 10) == parseInt(oidFormaCalcBaseCalc, 10) )  {
			if ( baseCalculo == "" ) {
				retorno = false;
            }
            else {
				if(parseInt(baseCalculo, 10) == parseInt(oidBaseCalcMonto, 10) ) {
					if(tipoVenta=="") {
						retorno = false;
                    }
                }
            }				
        }
    }
    return retorno;
}

function onClickParticipantes() {                  
	var obj = new Object();
    obj.opcionMenu = get('formulario.opcionMenu');
    var whnd = mostrarModalSICC('LPMantenerParametrosGerentes','cargar clasificacion participantes',obj);

	if (whnd != null) {
	    set ('formulario.hidLstClasConsul',whnd[0]);
        set ('formulario.hidListaClasif',whnd[1]);             
        set('formulario.conectorAction', 'LPMantenerParametrosGerentes');
        eval('formulario').oculto="S"; 
        set('formulario.accion', 'almacenar Clasificacion Participantes');
        enviaSICC('formulario','','','N');
    }
}

function onClickMontoVentaRecomendada() {
	var obj = new Object();
    obj.opcionMenu = get('formulario.opcionMenu');
    var whnd = mostrarModalSICC('LPMantenerParametrosGerentes','cargar monto venta recomendadas',obj,null,260);

	if (whnd != null){         
	    set('formulario.hidLstMVR', whnd[0]) ;
        set('formulario.conectorAction', 'LPMantenerParametrosGerentes');
        eval('formulario').oculto="S"; 
        set('formulario.accion', 'almacenar monto venta recomendadas');
        enviaSICC('formulario');
    }
}

function accionAceptar(){        
	var vOidFC = get('formulario.cbFormaCalculo');
    set('formulario.oidFormaCalculo', vOidFC);

	if ( vOidFC == get('formulario.oid_Forma_Calc_Base_Calc')) {
    // Agregado por ssantana, 1/6/2005 - Si existe un Oid de Base Calculo de la plantilla, se muestra 
    // y deshabilita el combo Forma de Calculo.
	    var oidBCPlantilla = get('formulario.oidPlantillaBaseCalculo');
        if ( oidBCPlantilla != "") {
	        marcaSeleccionDelUsuario('formulario.cbBaseCalculo', oidBCPlantilla );
            accion("formulario.cbBaseCalculo",".disabled=true");
            document.getElementById("capa2").style.visibility = "visible";

			if ( oidBCPlantilla == get('formulario.oidBC_Recomendada') ) {
				document.getElementById("capa1").style.visibility='visible';
            }
			else { 
				if( oidBCPlantilla == get('formulario.oidBC_Calculo_Monto') ) {
					document.getElementById("capa4").style.visibility='visible';

					//si existe tipo de venta en la plantilla
					var oidTipoVta = get('formulario.oidTipoVta');
					var oidTipoVentaPlantilla = get('formulario.oidTipoVentaPlantilla');
					if(oidTipoVentaPlantilla!="") {
						//se selecciona en el combo
						var oidSeleccionado = new Array();
						oidSeleccionado = oidTipoVentaPlantilla.split(",");	    
						set("formulario.cbTipoVenta", oidSeleccionado);

						//y se deshabilita
						accion("formulario.cbTipoVenta",".disabled=true");
					}
					else {
						if(oidTipoVta!="") {
							marcaSeleccionDelUsuario('formulario.cbTipoVenta', get('formulario.oidTipoVta'));
							accion("formulario.cbTipoVenta",".disabled=true");
						}
					}		
				}

				//Ocultar la capa Recomendadas con los campos:                
                document.getElementById("capa3").style.visibility='hidden';
            } 
        }
		else {
			//dtoConcurso.paramGerentes.oidFormaCalculo != ConstantesINC.OID_FORMA_CALC_BASE_CALC 
            document.getElementById("capa2").style.visibility='hidden';
            document.getElementById("capa3").style.visibility='hidden';//Recomendadas
            document.getElementById("capa4").style.visibility='hidden';         
        } 

        document.getElementById("capa2").style.visibility='visible';
    }
    else{
		document.getElementById("capa2").style.visibility='hidden';//Forma Calculo
        marcaSeleccionDelUsuario('formulario.cbBaseCalculo', "");

		document.getElementById("capa4").style.visibility='hidden';//Monto
        marcaSeleccionDelUsuario('formulario.cbTipoVenta', "");

		document.getElementById("capa3").style.visibility='hidden';//Recomendadas
        marcaSeleccionDelUsuario('formulario.cbPeriodoInicialEvaluacion', "");        
		set('formulario.chkRecomendacionEfectiva','');
        set('formulario.cbPeriodoInicialEvaluacion','');
        set('formulario.txtNumeroPeriodosEvaluar','');
        set('formulario.txtNumeroMinimoPedidos','');
        set('formulario.txtNumeroMinimoPedidosRecomendada','');

        if ( vOidFC == get('formulario.oid_Forma_Calc_Vbles_Vta')) {
			var obj = new Object();
            obj.oidVariablesVenta = get('formulario.oidVariablesVenta');
            obj.opcionMenu = get('formulario.opcionMenu');

            var whnd = mostrarModalSICC('LPMantenerParametrosGerentes','cargar variables venta',obj,null,230);      
            if (whnd != null) {
                      set('formulario.hidVariablesVenta', whnd[0]);
                      set('formulario.conectorAction', 'LPMantenerParametrosGerentes');
                      eval('formulario').oculto="S"; 
                      set('formulario.accion', 'almacenar variables venta');
                      enviaSICC('formulario','','','N');
            }
        }
    }
}

function onChangeBaseCalculo()    {
	if (get('formulario.cbBaseCalculo','V') == get('formulario.oidBC_Calculo_Monto'))
		document.getElementById("capa4").style.visibility='visible';//Monto
    else
		document.getElementById("capa4").style.visibility='hidden';//Monto
}

function accionGuardar () {
    if(get("formulario.cbFormaCalculo")==get("formulario.oid_Forma_Calc_Base_Calc") 
    && get('formulario.cbBaseCalculo')== get('formulario.oidBC_Calculo_Monto')){
		if(!sicc_validaciones_generales('Obligatorio')) {
			return;
        }	
    }

    if(!sicc_validaciones_generales('grupoCamposNumericos')) {
		return;
    }

    if (tomarDatosPantalla()) {
		var vDatosOblig = comprobarDatosObligatorios();
        set('formulario.datosObligatorios', vDatosOblig);
        set('formulario.conectorAction', 'LPMantenerParametrosGerentes');
        eval('formulario').oculto="S"; 
        set('formulario.accion', 'guardar');
        enviaSICC('formulario','','','N');
    }
}

function tomarDatosPantalla(){
	set('formulario.hidRecomendacionEfectiva',get('formulario.ckRecomendacionEfectiva'));
    set('formulario.hidPeriodoInicialEval',get('formulario.cbPeriodoInicialEvaluacion','V'));
    set('formulario.hidNroPeriodosEvaluar',get('formulario.txtNumPeriodosEvaluar'));
    set('formulario.hidNroMinimoPedidos',get('formulario.txtNumMinPedidos'));
    set('formulario.hidNroMinPedidosReco',get('formulario.txtNumMinPedidosRecomendada'));         
    set('formulario.oidTipoVta', get('formulario.cbTipoVenta'));

    if (document.getElementById("capa3").style.visibility =='hidden' ){
		//Si capa oculta, vacio la lista
        set('formulario.hidLstMVR','');
    }

    var vOidFC = get('formulario.cbFormaCalculo');

	if ( vOidFC == get('formulario.oid_Forma_Calc_Vbles_Vta')) {
		if( get('formulario.hidVariablesVenta') == '' && get('formulario.tengoVariablesVenta') == ''){
			GestionarMensaje('1498');
            return false;
        }
    }

    if (get('formulario.cbBaseCalculo','V') == get('formulario.oidBC_Recomendada')){
		if( get('formulario.hidLstMVR') == '' && get('formulario.tengoLstMVR') == ''){
			GestionarMensaje('1499');
            return false;
        }
    }

	return true;
}

function fGuardar(){
	accionGuardar();
}

function pasaMouse(valor, nombre) {
	var iPos = vConstantesPestInc[valor];
    activarPuntero('formulario',  iPos, nombre);
}

function haceClick(valor) {
	var iPos = vConstantesPestInc[valor];
    onClickPestanyaHabilitada(iPos, 'formulario', 'LPMantenerParametrosGerentes');
}

function accionError(){
	fMostrarMensajeError();
}

function accionAceptarFormaCalculo(){    
	//se toma el oid del combo base de calculo
	set('formulario.oidBaseCalculo', get('formulario.cbBaseCalculo','V'));

	//si la base de calculo es monto
    if (get('formulario.cbBaseCalculo','V') == get('formulario.oidBC_Calculo_Monto')){
		//muestra la capa de monto
		document.getElementById("capa4").style.visibility='visible';//Monto

		//si existe tipo de venta en la plantilla
		var oidTipoVta = get('formulario.oidTipoVta');
		var oidTipoVentaPlantilla = get('formulario.oidTipoVentaPlantilla');
		if(oidTipoVentaPlantilla!="") {
			//se selecciona en el combo
			var oidSeleccionado = new Array();
			oidSeleccionado = oidTipoVentaPlantilla.split(",");	    
			set("formulario.cbTipoVenta", oidSeleccionado);

			//y se deshabilita
            accion("formulario.cbTipoVenta",".disabled=true");
		}
		else {
			if(oidTipoVta!="") {
				marcaSeleccionDelUsuario('formulario.cbTipoVenta', get('formulario.oidTipoVta'));
	 		    accion("formulario.cbTipoVenta",".disabled=true");
			}
		}		

		//oculta la capa de recomendadas
        document.getElementById("capa3").style.visibility='hidden';//Recomendadas

		//se limpian los componentes de la capa recomendadas
        limpiaCapaRecomendados();
    }
    else{ // si la base de calculo no es monto
		set('formulario.cbTipoVenta','');
		// oculta la capa monto
        document.getElementById("capa4").style.visibility='hidden';//Monto

		// si la base de calculo es recomendada muestra la capa recomendadas
		if ( get('formulario.cbBaseCalculo','V') == get('formulario.oidBC_Recomendada'))
			document.getElementById("capa3").style.visibility='visible';//Recomendadas
        else { // si la base de calculo no es recomendada oculta la capa recomendadas
	        document.getElementById("capa3").style.visibility='hidden';//Recomendadas
            limpiaCapaRecomendados();
        }
    }
}

function limpiaCapaRecomendados(){
	set('formulario.ckRecomendacionEfectiva', 'N');
    set('formulario.cbPeriodoInicialEvaluacion', '');
    set('formulario.txtNumPeriodosEvaluar','');
    set('formulario.txtNumMinPedidos','');
    set('formulario.txtNumMinPedidosRecomendada','');         
}

function onClickPestanyaPaginaGerentes(valor, form, nombreLP) {
    if(get("formulario.cbFormaCalculo")==get("formulario.oid_Forma_Calc_Base_Calc") 
    && get('formulario.cbBaseCalculo')== get('formulario.oidBC_Calculo_Monto')){
		if(!sicc_validaciones_generales('Obligatorio')) {
			return;
        }	
    }

    if(!sicc_validaciones_generales('grupoCamposNumericos')) {
		return;
    }

    onClickPestanyaHabilitada(valor, form, nombreLP);
}
