  document.all["CpLin4listado1"].style.visibility='';
  document.all["prim1Div"].style.visibility='';
  document.all["ret1Div"].style.visibility='';
  document.all["ava1Div"].style.visibility='';
  document.all["ModificarDiv"].style.visibility='';
