//Agregado por incidencia 20760
function onChangeTipoCalificacion() {
	var valorElegido = get("frmFormulario.cbTipoCalificacion");

	if(valorElegido==get("frmFormulario.OID_TIPO_CALIF_PROYECTADA")) {
		var arraySeteo = new Array();
		arraySeteo[0] = get("frmFormulario.OID_DIRIGIDO_A_GERENTE");
		set("frmFormulario.cbDirigidoA", arraySeteo);
		accion('frmFormulario.cbDirigidoA','.disabled=true');
	}	
	else {
		accion('frmFormulario.cbDirigidoA','.disabled=false');
	}
}

function onClickProductosValidos() {
	if (get("frmFormulario.ckProductosValidosCompleto")=="S") {
		set("frmFormulario.ckProductosExcluidos", "N");
		set("frmFormulario.ckProductosBonificados", "N");
		set("frmFormulario.ckProductosExigidos", "N");

		accion('frmFormulario.ckProductosExcluidos','.disabled=true');
		accion('frmFormulario.ckProductosBonificados','.disabled=true');
		accion('frmFormulario.ckProductosExigidos','.disabled=true');
	}
	else {
		accion('frmFormulario.ckProductosExcluidos','.disabled=false');
		accion('frmFormulario.ckProductosBonificados','.disabled=false');
		accion('frmFormulario.ckProductosExigidos','.disabled=false');
	}
}
