             var nombrelistado="listado1";
             var nombreCbNivel= "cbNivelPremiacion";
             var indiceFila=0;
             eval(nombrelistado+".preparaCamposDR()");
             focaliza('frm'+nombrelistado+'.'+nombreCbNivel+'_'+indiceFila,'');  
         }
         else{

                document.selection.empty(); 
