PAGINA.JAVASCRIPT:

  function onLoad(){

    configurarMenuSecundario('Formulario');
    fMostrarMensajeError();
    deshabilitar();
    focaliza('Formulario.cbMarca');

  }

//------------------------------------------------------------------------------

  function deshabilitar(){

    accion('Formulario.cbPeriodo', '.disabled=true');
    accion('Formulario.txtCodVentaHasta', '.disabled=true');
    accion('Formulario.txtCodProductoHasta', '.disabled=true');
    accion('Formulario.txtPaginaHasta', '.disabled=true');

  }

//------------------------------------------------------------------------------

  function fLimpiar(){

    deshabilitar();
    focaliza('Formulario.cbMarca');

  }

//------------------------------------------------------------------------------

  function generar(formato){

    if(sicc_validaciones_generales()){

      var a = new Object();
      a.accion = 'generar';

      a.marca = get('Formulario.cbMarca');
      a.marcaDesc = get('Formulario.cbMarca','T');

      a.canal = get('Formulario.cbCanal');
      a.canalDesc = get('Formulario.cbCanal','T');

      a.periodo = get('Formulario.cbPeriodo');
      a.periodoDesc = get('Formulario.cbPeriodo','T');

      a.acceso = get('Formulario.cbAcceso');
      a.accesoDesc = get('Formulario.cbAcceso','T');

      a.productoDesde = get('Formulario.txtCodProductoDesde');
      a.productoHasta = get('Formulario.txtCodProductoHasta');

      a.ventaDesde = get('Formulario.txtCodVentaDesde');
      a.ventaHasta = get('Formulario.txtCodVentaHasta');

      descr = get('Formulario.txtDescripcion');

      if (descr != ''){
        a.descripcion = "%" + get('Formulario.txtDescripcion') + "%";
      }else{
        a.descripcion = descr;
      }



      a.catalogo = get('Formulario.cbCatalogo');

      a.paginaDesde = get('Formulario.txtPaginaDesde');
      a.paginaHasta = get('Formulario.txtPaginaHasta');

      a.operadorPrecioCatalogo = obtenerOperadorPrecioCatalogo();
      a.precioCatalogo = get('Formulario.txtPrecioCatalogo');

      a.tipoSolicitud = get('Formulario.cbTipoSolicitud');
      a.tipoLinea = get('Formulario.cbTipoLinea');

      a.operadorUnidadesTotales = obtenerOperadorUnidadesTotales();
      a.unidadesTotales = get('Formulario.txtUnidadesTotales');

      a.operadorUnidadesFaltantes = obtenerOperadorUnidadesFaltantes();
      a.unidadesFaltantes =  get('Formulario.txtUnidadesFaltantes');

      a.criterioOrdenacion1 = obtenerCriterioOrdenacion1();
      a.criterioOrdenacion2 = obtenerCriterioOrdenacion2();
      a.criterioOrdenacion3 = obtenerCriterioOrdenacion3();


      var vistaElegida = get('Formulario.cbVista');
      a.vista = vistaElegida;

      if (vistaElegida == '1'){
        reporte = 'RD_REP-044_01';
      }else{
        reporte = 'RD_REP-044';
      }

      a.reporte = reporte;
      a.formato = formato;

      a.criteriosSeleccionDesc = concatenarCriterios();

      mostrarModalSICC('PREEstadisticasMF','generar', a);

    }

  }

//------------------------------------------------------------------------------

  function concatenarCriterios(){

    marcaDesc = get('Formulario.cbMarca','T');
    canalDesc = get('Formulario.cbCanal','T');
    periodoDesc = get('Formulario.cbPeriodo','T');
    accesoDesc = get('Formulario.cbAcceso','T');

    ventaDesde = get('Formulario.txtCodVentaDesde');
    ventaHasta = get('Formulario.txtCodVentaHasta');

    productoDesde = get('Formulario.txtCodProductoDesde');
    productoHasta = get('Formulario.txtCodProductoHasta');

    descripcion = get('Formulario.txtDescripcion');

    catalogoDesc = get('Formulario.cbCatalogo','T');

    paginaDesde = get('Formulario.txtPaginaDesde');
    paginaHasta = get('Formulario.txtPaginaHasta');

    operadorPrecioCatalogo = obtenerOperadorPrecioCatalogo();
    precioCatalogo = get('Formulario.txtPrecioCatalogo');

    tipoSolicitudDesc = get('Formulario.cbTipoSolicitud','T');
    tipoLineaDesc = get('Formulario.cbTipoLinea','T');

    operadorUnidadesTotales = obtenerOperadorUnidadesTotales();
    unidadesTotales = get('Formulario.txtUnidadesTotales');

    operadorUnidadesFaltantes = obtenerOperadorUnidadesFaltantes();
    unidadesFaltantes =  get('Formulario.txtUnidadesFaltantes');

    vistaDesc = get('Formulario.cbVista','T');

    criterioOrdenacion1 = get('Formulario.cbCriterioOrdenacion1','T');
    criterioOrdenacion2 = get('Formulario.cbCriterioOrdenacion2','T');
    criterioOrdenacion3 = get('Formulario.cbCriterioOrdenacion3','T');


    var criteriosSeleccionDesc = marcaDesc + "--" + canalDesc + "--" + periodoDesc + "--" + accesoDesc;


    if (ventaDesde != '')
      criteriosSeleccionDesc += "--" + ventaDesde;

    if (ventaHasta != '')
      criteriosSeleccionDesc += "--" + ventaHasta;

    if (productoDesde != '')
      criteriosSeleccionDesc += "--" + productoDesde;

    if (productoHasta != '')
      criteriosSeleccionDesc += "--" + productoHasta;

    if (descripcion != '')
      criteriosSeleccionDesc += "--" + descripcion;

    if (catalogoDesc != '')
      criteriosSeleccionDesc += "--" + catalogoDesc;

    if (paginaDesde != '')
      criteriosSeleccionDesc += "--" + paginaDesde;

    if (paginaHasta != '')
      criteriosSeleccionDesc += "--" + paginaHasta;

    if (operadorPrecioCatalogo != '')
      criteriosSeleccionDesc += "--" + operadorPrecioCatalogo;

    if (precioCatalogo != '')
      criteriosSeleccionDesc += "--" + precioCatalogo;

    if (tipoSolicitudDesc != '')
      criteriosSeleccionDesc += "--" + tipoSolicitudDesc;

    if (tipoLineaDesc != '')
      criteriosSeleccionDesc += "--" + tipoLineaDesc;

    if (operadorUnidadesTotales != '')
      criteriosSeleccionDesc += "--" + operadorUnidadesTotales;

    if (unidadesTotales != '')
      criteriosSeleccionDesc += "--" + unidadesTotales;

    if (operadorUnidadesFaltantes != '')
      criteriosSeleccionDesc += "--" + operadorUnidadesFaltantes;

    if (unidadesFaltantes != '')
      criteriosSeleccionDesc += "--" + unidadesFaltantes;

    criteriosSeleccionDesc += "--" + vistaDesc;

    if (criterioOrdenacion1 != '')
      criteriosSeleccionDesc += "--" + criterioOrdenacion1;

    if (criterioOrdenacion2 != '')
      criteriosSeleccionDesc += "--" + criterioOrdenacion2;

    if (criterioOrdenacion3 != '')
      criteriosSeleccionDesc += "--" + criterioOrdenacion3;

    return criteriosSeleccionDesc;


  }

//------------------------------------------------------------------------------

  function obtenerOperadorPrecioCatalogo(){

    var operador = parseInt(get('Formulario.cbOperadorPrecioCatalogo'),10);
    return (devolverOperador(operador));

  }

//------------------------------------------------------------------------------

  function obtenerOperadorUnidadesTotales(){

    var operador = parseInt(get('Formulario.cbOperadorUnidadesTotales'),10);
    return (devolverOperador(operador));

  }

//------------------------------------------------------------------------------

  function obtenerOperadorUnidadesFaltantes(){

    var operador = parseInt(get('Formulario.cbOperadorUnidadesFaltantes'),10);
    return (devolverOperador(operador));

  }

//------------------------------------------------------------------------------

  function devolverOperador(operador){

    switch (operador) {

      case 1:
        return ("'<'");
      case 2:
        return ("'>'");
      case 3:
        return ("'='");
      case 4:
        return ("'<='");
      case 5:
        return ("'>='");
      default:
        return ("");

    }

  }

//------------------------------------------------------------------------------

  function obtenerCriterioOrdenacion1(){

    var criterio = parseInt(get('Formulario.cbCriterioOrdenacion1'),10);
    return (devolverValorCriterio(criterio));

  }

//------------------------------------------------------------------------------

  function obtenerCriterioOrdenacion2(){

    var criterio = parseInt(get('Formulario.cbCriterioOrdenacion2'),10);
    return (devolverValorCriterio(criterio));

  }

//------------------------------------------------------------------------------

  function obtenerCriterioOrdenacion3(){

    var criterio = parseInt(get('Formulario.cbCriterioOrdenacion3'),10);
    return (devolverValorCriterio(criterio));

  }

//------------------------------------------------------------------------------


  function devolverValorCriterio(criterio){

    switch (criterio) {

      case 1:
        return ("',UNID_TOTALES'");
      case 2:
        return ("',VAL_CODI_VENT'");
      case 3:
        return ("',COD_SAP'");
      case 4:
        return ("',NUM_PAGI_CATA'");
      default:
        return ("");

    }

  }

//------------------------------------------------------------------------------

  function cargarPeriodos(){

    pais = get('Formulario.pais');
    marca = get('Formulario.cbMarca');
    canal = get('Formulario.cbCanal');

    if (pais != '' && marca != '' && canal != ''){

      var arr = new Array();

      arr[arr.length] = ["pais", pais];
      arr[arr.length] = ["marca", marca];
      arr[arr.length] = ["canal", canal];

      recargaCombo("Formulario.cbPeriodo", "PRECargarPeriodos", "es.indra.sicc.dtos.cra.DTOPeriodo",arr);
      accion('Formulario.cbPeriodo', '.disabled=false');

    }else{

      var aCombo = new Array(new Array("",""));

      set_combo('Formulario.cbPeriodo',aCombo, []);
      accion('Formulario.cbPeriodo', '.disabled=true');

    }

  }

//------------------------------------------------------------------------------

  function onchangeVista(){

    vista = get('Formulario.cbVista');

    if(vista == '1'){

      accion('Formulario.cbCriterioOrdenacion1', '.disabled=false');
      accion('Formulario.cbCriterioOrdenacion2', '.disabled=false');
      accion('Formulario.cbCriterioOrdenacion3', '.disabled=false');

    }else{

      accion('Formulario.cbCriterioOrdenacion1', '.disabled=true');    
      accion('Formulario.cbCriterioOrdenacion2', '.disabled=true');    
      accion('Formulario.cbCriterioOrdenacion3', '.disabled=true');    

      set('Formulario.cbCriterioOrdenacion1','');
      set('Formulario.cbCriterioOrdenacion2','');
      set('Formulario.cbCriterioOrdenacion3','');

    }

  }

//------------------------------------------------------------------------------

  function habilitarCodigoVentaHasta(){

    if(get('Formulario.txtCodVentaDesde') != ''){
      accion('Formulario.txtCodVentaHasta', '.disabled=false');
      focaliza('Formulario.txtCodVentaHasta');
    }else{
      accion('Formulario.txtCodVentaHasta', '.disabled=true');
      focaliza('Formulario.txtCodProductoDesde');
    }

  }

//------------------------------------------------------------------------------

  function habilitarCodigoProductoHasta(){

    if(get('Formulario.txtCodProductoDesde') != ''){
      accion('Formulario.txtCodProductoHasta', '.disabled=false');
      focaliza('Formulario.txtCodProductoHasta');
    }else{
      accion('Formulario.txtCodProductoHasta', '.disabled=true');
      focaliza('Formulario.txtDescripcion');
    }

  }

//------------------------------------------------------------------------------

  function habilitarPaginaHasta(){

    if(get('Formulario.txtPaginaDesde') != ''){
      accion('Formulario.txtPaginaHasta', '.disabled=false');
      focaliza('Formulario.txtPaginaHasta');
    }else{
      accion('Formulario.txtPaginaHasta', '.disabled=true');
      focaliza('Formulario.cbOperadorPrecioCatalogo');
    }

  }

//------------------------------------------------------------------------------


  function cambioOperadorCatalogo(){

    var operadorCatalogo = get('Formulario.cbOperadorPrecioCatalogo');

    if(operadorCatalogo != ''){
      sicc_cambiar_estado('txtPrecioCatalogo', true);
    }else{
      sicc_cambiar_estado('txtPrecioCatalogo', false);
    }

  }

//------------------------------------------------------------------------------

  function cambioPrecioCatalogo(){

    var precioCatalogo = get('Formulario.txtPrecioCatalogo');

    if(precioCatalogo != ''){
      sicc_cambiar_estado('cbOperadorPrecioCatalogo', true);
    }else{
      sicc_cambiar_estado('cbOperadorPrecioCatalogo', false);
    }

  }


//------------------------------------------------------------------------------

  function cambioOperadorTotales(){

    var operadorTotales = get('Formulario.cbOperadorUnidadesTotales');

    if(operadorTotales != ''){
      sicc_cambiar_estado('txtUnidadesTotales', true);
    }else{
      sicc_cambiar_estado('txtUnidadesTotales', false);
    }

  }

//------------------------------------------------------------------------------

  function cambioUnidadesTotales(){

    var unidadesTotales = get('Formulario.txtUnidadesTotales');

