    var operadorFaltantes = get('Formulario.cbOperadorUnidadesFaltantes');

    if(operadorFaltantes != ''){
      sicc_cambiar_estado('txtUnidadesFaltantes', true);
    }else{
      sicc_cambiar_estado('txtUnidadesFaltantes', false);
    }

  }

//------------------------------------------------------------------------------

  function cambioUnidadesFaltantes(){

    var unidadesFaltantes = get('Formulario.txtUnidadesFaltantes');

    if(unidadesFaltantes != ''){
      sicc_cambiar_estado('cbOperadorUnidadesFaltantes', true);
