}
//Agregado por cambio 20090929
function recargaLineas(datos){
	 var opcionMenu = get("formulario.opcionMenu"); 
    seteo = cargaArray(datos);
	var validaDeud =	get("formulario.validaDeuda");
    var validaDeudaDescrip =  get('formulario.validaDeudaDescrip');
	if(opcionMenu=="Consultar Concurso") {
	   //alert("opcionMenu: " + opcionMenu);
	   //alert("validaDeud: " + validaDeud +"validaDeudaDescrip: " + validaDeudaDescrip);
	   set_combo("formulario.cbValidaDeuda", [[validaDeud,validaDeudaDescrip]]);
	}  else if(opcionMenu=="Modificar Concurso") {
	   //alert("opcionMenu: " + opcionMenu);
	   //alert("validaDeud: " + validaDeud +"validaDeudaDescrip: " + validaDeudaDescrip);
	   var arrayValidaDeuda = new Array();
       arrayValidaDeuda[0] = validaDeud;
	   set_combo('formulario.cbValidaDeuda', seteo);
       set("formulario.cbValidaDeuda", arrayValidaDeuda);
	}
	if(opcionMenu!="Consultar Concurso" && opcionMenu!="Modificar Concurso"){
		  if(validaDeud == "" && validaDeudaDescrip == ""){	
			set_combo('formulario.cbValidaDeuda', seteo);
          } else{
				 var arrayValidaDeuda = new Array();
				 arrayValidaDeuda[0] = validaDeud;
				 set_combo('formulario.cbValidaDeuda', seteo);
				 set("formulario.cbValidaDeuda", arrayValidaDeuda);
		  }
	}
}
function cargaArray(datos){
     var seteo = new Array(datos.length + 1);
    seteo[0] = ['' , 'No Se Valida Deuda'];
    for(i = 0; i < datos.length; i++){
      seteo[i+1] = [ datos[i][0], datos[i][1] ]
    }
    return seteo;
}


function fVolver() {
         window.close();
}

function setearValoresCamposPantalla() {
         var oidRequisitosPremiacion = get("formulario.oidRequisitosPremiacion");
         var montoMinimoConcurso = get("formulario.montoMinimoConcurso");
         var numeroPedidos = get("formulario.numeroPedidos");
         var cuotaMinimaIngreso = get("formulario.cuotaMinimaIngreso");
         var pasarPedidosPeriodoPremiacion = get("formulario.pasarPedidosPeriodoPremiacion");
         var montoMinimoPedido = get("formulario.montoMinimoPedido");
         var pagoTiempo = get("formulario.pagoTiempo");
         var diasGracia = get("formulario.diasGracia");
         var periodoDesde = get("formulario.periodoDesde");
         var periodoHasta = get("formulario.periodoHasta");
		 var validaDeud =	get("formulario.validaDeuda");
		 var validaDeudaDescrip =  get('formulario.validaDeudaDescrip');
		 var numPerEspera = get("formulario.numPerioEspera");
		 var perioMaximo =  get("formulario.perioMax");
		 var perioMaxDes =    get("formulario.perioMaxDes");		 
		 //alert("validaDeud: " + validaDeud + "numPerEspera: "+ numPerEspera + "perioMaximo: "+ perioMaximo + "perioMaxDes: " + perioMaxDes );

         set("formulario.oidRequisitosPremiacion", oidRequisitosPremiacion);
         set("formulario.txtMontoMinConcurso", montoMinimoConcurso);
         set("formulario.txtNumPedidos", numeroPedidos);
         set("formulario.txtCuotaMinIngreso", cuotaMinimaIngreso);

         if(pasarPedidosPeriodoPremiacion=="true") set("formulario.ckPasarPedido", "S");
         if(pasarPedidosPeriodoPremiacion=="false") set("formulario.ckPasarPedido", "N");

         set("formulario.txtMontoMinPedido", montoMinimoPedido);

         if(pagoTiempo=="true") set("formulario.ckPagoTiempo", "S");
         if(pagoTiempo=="false") set("formulario.ckPagoTiempo", "N");

         set("formulario.txtDiasGracia", diasGracia);

         var arraySeteoDesde = new Array();
         arraySeteoDesde[0] = periodoDesde;
         set("formulario.cbPeriodoDesde", arraySeteoDesde);

         var arraySeteoHasta = new Array();
         arraySeteoHasta[0] = periodoHasta;
         set("formulario.cbPeriodoHasta", arraySeteoHasta);
		 var arrayValidaDeuda = new Array();
		 arrayValidaDeuda[0] = validaDeud; 	

		 //Agregado por cambio 20090929
		 if(numPerEspera != ""){
			set("formulario.txtNumeroPedidoEspera", numPerEspera);
		 }

		 set_combo("formulario.cbPromedioMaximoAsignacion", [[perioMaximo, perioMaxDes]])

		 if(validaDeud == ""){
			document.getElementById("txtNumeroPedidoEspera").disabled = true;
			document.getElementById("cbPromedioMaximoAsignacion").disabled = true;
		 }else{			
			document.getElementById("txtNumeroPedidoEspera").disabled = false;
			document.getElementById("cbPromedioMaximoAsignacion").disabled = false;	
		}



}	

function fLimpiar() {
         onLoadPag();
}

function focoMontoConcurso() {
         focaliza("formulario.txtMontoMinConcurso");
}

