}

function fGuardar() {
         accionGuardar();
}

function accionGuardar() {
         //Cleal - 21049
         if(get('formulario.ckPagoTiempo')=="S") {
                  if(!sicc_validaciones_generales('grupoCamposObligatorios')) {
                           return;
                  }	
         }

		 if(get('formulario.cbValidaDeuda')!="" && !sicc_validaciones_generales('grupoValidaDeuda')) {
			return;
		 }

         if(!sicc_validaciones_generales('grupoCamposNumericos')) {
                  return;
         }

		 // sapaza -- PER-SiCC-2010-0395 -- 15/07/2010
         if(get('formulario.ckPasarPedido')=="S") { 
			  var montoMinimoPedido = get("formulario.txtMontoMinPedido");

			  if(montoMinimoPedido < 1) {
  			    GestionarMensaje('INC057');
				focaliza("formulario.txtMontoMinPedido");
				return;
			  }
         }

         tomarValoresCamposPantalla();
         eval("formulario").oculto= "S";
         set("formulario.conectorAction", "LPMantenerRequisitosPremiacion");
         set("formulario.accion", "guardar");
         enviaSICC('formulario', '', '', 'N');
}

function tomarValoresCamposPantalla() {
         var oidRequisitosPremiacion = get("formulario.oidRequisitosPremiacion");
         var montoMinimoConcurso = get("formulario.txtMontoMinConcurso");
         var numeroPedidos = get("formulario.txtNumPedidos");
         var cuotaMinimaIngreso = get("formulario.txtCuotaMinIngreso");
         var pasarPedidosPeriodoPremiacion = get("formulario.ckPasarPedido");
         var montoMinimoPedido = get("formulario.txtMontoMinPedido");
         var pagoTiempo = get("formulario.ckPagoTiempo");
         var diasGracia = get("formulario.txtDiasGracia");
         var periodoDesde = get("formulario.cbPeriodoDesde");
         var periodoHasta = get("formulario.cbPeriodoHasta");
         var listaProductosExigidosPremiacion = get("formulario.listaProductosExigidosPremiacion");
         var lstCursosExigidosPremiacion = get("formulario.lstCursosExigidosPremiacion");
         var datosObligatorios = comprobarDatosObligatorios();
		 //Agregado por cambio 20090929
		 var validarDeuda = get('formulario.cbValidaDeuda');
		 var validaDeudaDescrip =  get('formulario.cbValidaDeuda','T');
		 var numPerioEspe = get('formulario.txtNumeroPedidoEspera');
		 //alert("numPerioEspe: " + numPerioEspe);
		 var perioMax = get('formulario.cbPromedioMaximoAsignacion');
 		 var perioMaxDes = get('formulario.cbPromedioMaximoAsignacion','T');


         var opcionMenu = get("formulario.opcionMenu");

         set("formulario.oidRequisitosPremiacion", oidRequisitosPremiacion);
         set("formulario.montoMinimoConcurso", montoMinimoConcurso);
         set("formulario.numeroPedidos", numeroPedidos);
         set("formulario.cuotaMinimaIngreso", cuotaMinimaIngreso);
         set("formulario.pasarPedidosPeriodoPremiacion", pasarPedidosPeriodoPremiacion);
         set("formulario.montoMinimoPedido", montoMinimoPedido);
         set("formulario.pagoTiempo", pagoTiempo);
         set("formulario.diasGracia", diasGracia);
         set("formulario.periodoDesde", periodoDesde);
         set("formulario.periodoHasta", periodoHasta);
         set("formulario.listaProductosExigidosPremiacion", listaProductosExigidosPremiacion);
         set("formulario.lstCursosExigidosPremiacion", lstCursosExigidosPremiacion);
         set("formulario.datosObligatorios", datosObligatorios);
		 //Agregado por cambio 20090929	
		 set("formulario.validaDeuda", validarDeuda);
		 set("formulario.validaDeudaDescrip", validaDeudaDescrip);

		 if(numPerioEspe != ""){
			//alert("numPerioEspe: " + numPerioEspe);
			set("formulario.numPerioEspera", numPerioEspe);			
		 }

		 set("formulario.perioMax", perioMax);
		 set("formulario.perioMaxDes", perioMaxDes);

		// alert("Tomar valores");
		// alert("validarDeuda: " + validarDeuda + "numPerioEspe: " + numPerioEspe + "perioMax: " + perioMax + "perioMaxDes: " + perioMaxDes);

         set("formulario.opcionMenu", opcionMenu);
}

function onClickPestanyaPaginaRequisitosPremiacion(valor, form, nombreLP) {
	if(get("formulario.opcionMenu")!="Consultar Concurso") {
	     //Cleal - 21049
         if(get('formulario.ckPagoTiempo')=="S") {
                  if(!sicc_validaciones_generales('grupoCamposObligatorios')) {
                           return;
                  }	
         }

         if(!sicc_validaciones_generales('grupoCamposNumericos')) {
                  return;
         }

		 // sapaza -- PER-SiCC-2010-0395 -- 15/07/2010
         if(get('formulario.ckPasarPedido')=="S") { 
			  var montoMinimoPedido = get("formulario.txtMontoMinPedido");

			  if(montoMinimoPedido < 1) {
  			    GestionarMensaje('INC057');
				focaliza("formulario.txtMontoMinPedido");
				return;
			  }
         }

         tomarValoresCamposPantalla();	
         onClickPestanyaHabilitada(valor, form, nombreLP);
	}
	else {
	    tomarValoresCamposPantalla();	
        onClickPestanyaHabilitada(valor, form, nombreLP);
	}    
}

function comprobarDatosObligatorios() {
         if(get('formulario.ckPagoTiempo')=="S") {
                  if((get("formulario.cbPeriodoDesde")!="") && (get("formulario.cbPeriodoHasta")!="")) {	
                           return true;
                  }	
                  else {
                           return false;
                  }	
         }
         else {
                  return true;
         }		
}

function onChangePasarPedidoPeriodoPremiacion(accion) {
         var valor = get("formulario.ckPasarPedido");
         if(valor=="S") {
         		  if(accion=="combo")
         		  	set("formulario.txtMontoMinPedido", "1"); // sapaza -- PER-SiCC-2010-0395 -- 15/07/2010

                  document.getElementById("txtMontoMinPedido").disabled = false;
                  document.all["btnProductoExigidoPremiacion"].style.visibility='';
         }
         else {
                  set("formulario.txtMontoMinPedido", "");
                  document.getElementById("txtMontoMinPedido").disabled = true;
                  document.all["btnProductoExigidoPremiacion"].style.visibility='hidden';
         }
}

function onChangePagoTiempo() {
         var valor = get("formulario.ckPagoTiempo");
         if(valor=="S") {
                  document.getElementById("txtDiasGracia").disabled = false;
                  document.getElementById("cbPeriodoDesde").disabled = false;
                  document.getElementById("cbPeriodoHasta").disabled = false;		
         }
         else {
                  var arraySeteo = new Array();
                  set("formulario.cbPeriodoDesde", arraySeteo);
                  set("formulario.cbPeriodoHasta", arraySeteo);
                  set("formulario.txtDiasGracia", "");

                  document.getElementById("txtDiasGracia").disabled = true;
                  document.getElementById("cbPeriodoDesde").disabled = true;
                  document.getElementById("cbPeriodoHasta").disabled = true;		
         }
}

function reInicio() {
         eval("formulario").oculto= "N";
         focoMontoConcurso();
}

function errorEnAlmacenar() {
         focoMontoConcurso();
}

function onClickCursosExigidosPremiacion() {
                  var opcionMenu = get('formulario.opcionMenu');
                  var parametros = new Object();
                  parametros.opcionMenu = opcionMenu;

                  var lstCursosExigidosPremiacion = mostrarModalSICC("LPMantenerRequisitosPremiacion", "cargar cursos exigidos premiacion", parametros,800,600);

                  if(lstCursosExigidosPremiacion != undefined){
                           set("formulario.lstCursosExigidosPremiacion", lstCursosExigidosPremiacion);
                           eval("formulario").oculto= "S";
                           set("formulario.conectorAction", "LPMantenerRequisitosPremiacion");
                           set("formulario.accion", "almacenar cursos exigidos premiacion");
                           enviaSICC('formulario', '', '', 'N');
                  }		
}

function onClickProductoExigidoPremiacion() {
                  var opcionMenu = get('formulario.opcionMenu');
                  var parametros = new Object();
                  parametros.opcionMenu = opcionMenu;

                  var listaProductosExigidosPremiacion = mostrarModalSICC("LPMantenerRequisitosPremiacion", "cargar productos exigidos premiacion", parametros,800,600);

                  if(listaProductosExigidosPremiacion != undefined){			
                           set("formulario.listaProductosExigidosPremiacion", listaProductosExigidosPremiacion);	
                           eval("formulario").oculto= "S";
                           set("formulario.conectorAction", "LPMantenerRequisitosPremiacion");
                           set("formulario.accion", "almacenar productos exigidos premiacion");
                           enviaSICC('formulario', '', '', 'N');
                  }	
}

//Agregado por cambio 20090929
function onChangeCbValidaDeuda(){
	var aux = get('formulario.cbValidaDeuda', "T");
	var opcionMenu = get('formulario.opcionMenu');
	if(aux == "No Se Valida Deuda"){
			 document.getElementById("txtNumeroPedidoEspera").disabled = true;
             document.getElementById("cbPromedioMaximoAsignacion").disabled = true;
			 set('formulario.txtNumeroPedidoEspera', '');
			 set('formulario.cbPromedioMaximoAsignacion', ["",""]);
	}else{			  

             var numeroEspera = get('formulario.txtNumeroPedidoEspera');		

			 if(numeroEspera == ""){
				set('formulario.txtNumeroPedidoEspera', '1');
				numeroEspera = 1;
			 }

			 set('formulario.numPerioEspera',numeroEspera);
   		     document.getElementById("txtNumeroPedidoEspera").disabled = false;
             document.getElementById("cbPromedioMaximoAsignacion").disabled = false;	
			 eval("formulario").oculto= "S";
			 set("formulario.conectorAction", "LPMantenerRequisitosPremiacion");
			 set("formulario.accion", "periodoMaximo");
			 set("formulario.opcionMenu",opcionMenu );
             enviaSICC('formulario', '', '', 'N');
	}
}
 //Agregado por cambio 20090929
function onChangeObtenerPeriodoMaximo(){

	 var opcionMenu = get('formulario.opcionMenu');  	
	 var numeroEspera = get('formulario.txtNumeroPedidoEspera');	
	 set('formulario.numPerioEspera',numeroEspera);

	 if(numeroEspera != ""){
		 if(!sicc_validaciones_generales('grupoCamposNumericos')) {
			  return;
		 }
		 eval("formulario").oculto= "S";
		 set("formulario.conectorAction", "LPMantenerRequisitosPremiacion");
		 set("formulario.accion", "periodoMaximo");
		 set("formulario.opcionMenu",opcionMenu );
		 enviaSICC('formulario', '', '', 'N');

	 } else {
		 set_combo('formulario.cbPromedioMaximoAsignacion', [["",""]]);	
	 }


 }
 function fCargaComboMaximoPeriodo(oid, des){
		if(oid != ""){	
		    set('formulario.oidPerioMX', oid);
			set('formulario.desPeroMX', des);
			set_combo('formulario.cbPromedioMaximoAsignacion', [[oid,des]]);		
		}else{
			set_combo('formulario.cbPromedioMaximoAsignacion', [["",""]]);		
		}
 }
 function fBuscarPeriodoErro(){
	set_combo('formulario.cbPromedioMaximoAsignacion', [["",""]]);		
 }
