      }
  }

  function focalizarOnShTabBSS()  {
    try {
      document.all.btnAprobar.focus();
    } catch (e)  {
        try  {
          document.all.btnCalcular.focus();
        } catch (e)  {
            document.all.btnDetalle.focus();            
          }
      }
  }

  function focalizarOnTabBSS()  {
