  function accionBuscarCliente()  {
    var obj = new Object();
    var whnd = mostrarModalSICC('LPBusquedaRapidaCliente','',obj);
    if (whnd != null)  {
      var oid = whnd[0];
      var cod = whnd[1];
      set("formulario.txtCodCliente",cod);
      set("formulario.hOidCliente",oid);
    }
  }
